css:
.cta-pastel-bubble {
    font-family: 'Orbitron', sans-serif;
    background-color: #a0b4c0; /* Pale Teal */
    color: #fff;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    padding: 50px 20px;
    text-align: center;
    position: relative;
    overflow: hidden;
}

.cta-content {
    position: relative;
    z-index: 2;
    max-width: 650px;
}

.cta-pastel-bubble h2 {
    font-size: 2.8em;
    margin-bottom: 15px;
    font-weight: 700;
    text-shadow: 1px 1px 3px rgba(0,0,0,0.2);
}

.cta-pastel-bubble p {
    font-size: 1.15em;
    margin-bottom: 30px;
    line-height: 1.7;
    color: rgba(255, 255, 255, 0.9);
}

.cta-button {
    background-color: #e0c0a0; /* Muted Mustard */
    color: #333;
    border: none;
    padding: 17px 45px;
    border-radius: 35px;
    font-size: 1.25em;
    font-weight: 700;
    cursor: pointer;
    transition: background-color 0.3s ease, transform 0.2s ease;
    box-shadow: 0 5px 15px rgba(224, 192, 160, 0.5);
}

.cta-button:hover {
    background-color: #c0a080; /* Darker Muted Mustard */
    transform: translateY(-3px);
}

.cta-bubbles {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    overflow: hidden;
    z-index: 1;
}

.bubble {
    position: absolute;
    background-color: rgba(255, 255, 255, 0.15);
    border-radius: 50%;
    animation: float 6s ease-in-out infinite;
}

.bubble:nth-child(1) {
    width: 80px;
    height: 80px;
    top: 10%;
    left: 20%;
    animation-delay: -2s;
    background-color: rgba(255, 255, 255, 0.2);
}

.bubble:nth-child(2) {
    width: 120px;
    height: 120px;
    top: 40%;
    left: 60%;
    animation-delay: -4s;
}

.bubble:nth-child(3) {
    width: 60px;
    height: 60px;
    top: 70%;
    left: 40%;
    animation-delay: -3s;
    background-color: rgba(255, 255, 255, 0.1);
}

@keyframes float {
    0% { transform: translateY(0) scale(1); opacity: 0.7; }
    50% { transform: translateY(-50px) scale(1.1); opacity: 0.5; }
    100% { transform: translateY(0) scale(1); opacity: 0.7; }
}

@media (min-width: 768px) {
    .cta-pastel-bubble {
        flex-direction: row;
        justify-content: space-around;
        padding: 80px 40px;
    }
    .cta-content {
        flex: 1;
        max-width: 60%;
        text-align: left;
    }
    .cta-pastel-bubble h2 {
        font-size: 3.5em;
    }
    .cta-pastel-bubble p {
        font-size: 1.2em;
    }
    .cta-bubbles {
        flex: 1;
        display: flex;
        justify-content: center;
        align-items: center;
    }
    .bubble {
        position: relative;
        animation: none;
    }
    .bubble:nth-child(1) { top: auto; left: auto; animation-delay: 0s; }
    .bubble:nth-child(2) { top: auto; left: auto; animation-delay: 0s; }
    .bubble:nth-child(3) { top: auto; left: auto; animation-delay: 0s; }
}

@media (min-width: 1024px) {
    .cta-pastel-bubble {
        padding: 100px 60px;
    }
    .cta-content {
        max-width: 55%;
    }
    .cta-pastel-bubble h2 {
        font-size: 4em;
    }
}
