css:
.cta-rounded-promo {
    font-family: 'Orbitron', sans-serif;
    background-color: #e0d8c0; /* Pale Mustard */
    color: #333;
    padding: 50px 20px;
    text-align: center;
    position: relative;
    overflow: hidden;
}

.cta-content {
    position: relative;
    z-index: 2;
    max-width: 650px;
    margin: 0 auto;
    background-color: rgba(255, 255, 255, 0.7);
    padding: 30px;
    border-radius: 30px;
    box-shadow: 0 8px 16px rgba(0,0,0,0.1);
}

.cta-rounded-promo h2 {
    font-size: 2.8em;
    margin-bottom: 15px;
    color: #505050;
    font-weight: 700;
}

.cta-rounded-promo p {
    font-size: 1.1em;
    margin-bottom: 30px;
    line-height: 1.7;
    color: #555;
}

.cta-input-group {
    display: flex;
    flex-direction: column;
    gap: 15px;
    margin-top: 20px;
}

.cta-input-group input[type="email"] {
    padding: 15px 20px;
    border: 2px solid #d4af37; /* Mustard Yellow */
    border-radius: 30px;
    font-size: 1em;
    background-color: #fff;
    transition: border-color 0.3s ease;
}

.cta-input-group input[type="email"]:focus {
    outline: none;
    border-color: #b0902a;
}

.cta-input-group button {
    background-color: #dcd0ff; /* Soft Lavender */
    color: #333;
    border: none;
    padding: 15px 30px;
    border-radius: 30px;
    font-size: 1.1em;
    font-weight: 700;
    cursor: pointer;
    transition: background-color 0.3s ease, transform 0.2s ease;
    box-shadow: 0 4px 8px rgba(220, 208, 255, 0.4);
}

.cta-input-group button:hover {
    background-color: #c0b0e0; /* Darker Soft Lavender */
    transform: translateY(-2px);
}

.cta-graphic-element img {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    object-fit: cover;
    opacity: 0.2;
    z-index: 1;
}

@media (min-width: 768px) {
    .cta-rounded-promo {
        display: flex;
        align-items: center;
        justify-content: space-between;
        padding: 80px 40px;
        text-align: left;
    }
    .cta-content {
        flex: 1;
        max-width: 50%;
        margin: 0;
        padding: 40px;
    }
    .cta-rounded-promo h2 {
        font-size: 3.5em;
    }
    .cta-rounded-promo p {
        font-size: 1.2em;
    }
    .cta-input-group {
        flex-direction: row;
        max-width: 450px;
        margin: 30px 0 0;
    }
    .cta-input-group input[type="email"] {
        flex-grow: 1;
    }
    .cta-graphic-element {
        flex: 1;
        height: 350px;
        max-width: 45%;
        position: relative;
    }
    .cta-graphic-element img {
        border-radius: 25px;
    }
}

@media (min-width: 1024px) {
    .cta-rounded-promo {
        padding: 100px 60px;
    }
    .cta-content {
        padding: 50px;
    }
    .cta-rounded-promo h2 {
        font-size: 4em;
    }
}
