css:
.cta-silver-accent {
    font-family: 'Exo 2', sans-serif;
    background-color: #f0e6e0; /* Dusty Rose */
    color: #333;
    padding: 50px 20px;
    text-align: center;
    position: relative;
    overflow: hidden;
}

.cta-container {
    position: relative;
    z-index: 2;
    background-color: rgba(255, 255, 255, 0.8);
    padding: 30px;
    border-radius: 20px;
    max-width: 600px;
    margin: 0 auto;
    box-shadow: 0 6px 12px rgba(0,0,0,0.1);
}

.cta-silver-accent h2 {
    font-size: 2.7em;
    margin-bottom: 15px;
    color: #5a4a5a;
    font-weight: 700;
}

.cta-silver-accent p {
    font-size: 1.1em;
    margin-bottom: 30px;
    line-height: 1.6;
    color: #555;
}

.cta-silver-button {
    background-color: #c0c0c0; /* Silver */
    color: #333;
    border: none;
    padding: 15px 40px;
    border-radius: 30px;
    font-size: 1.2em;
    font-weight: 700;
    cursor: pointer;
    transition: background-color 0.3s ease, transform 0.2s ease, box-shadow 0.3s ease;
    box-shadow: 0 4px 10px rgba(192, 192, 192, 0.5);
}

.cta-silver-button:hover {
    background-color: #a9a9a9; /* Darker Silver */
    transform: translateY(-3px);
    box-shadow: 0 6px 15px rgba(192, 192, 192, 0.7);
}

.cta-accent-line {
    position: absolute;
    top: 50%;
    left: 0;
    width: 100%;
    height: 4px;
    background-color: #b0c4de; /* Pale Teal */
    transform: translateY(-50%) rotate(-2deg);
    z-index: 1;
}

.cta-image-placeholder img {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    object-fit: cover;
    opacity: 0.2;
    z-index: 0;
    border-radius: 0 0 20px 20px;
}

@media (min-width: 768px) {
    .cta-silver-accent {
        display: flex;
        align-items: center;
        justify-content: space-between;
        padding: 80px 40px;
    }
    .cta-container {
        flex: 1;
        max-width: 55%;
        margin: 0;
        text-align: left;
        padding: 40px;
    }
    .cta-silver-accent h2 {
        font-size: 3.5em;
    }
    .cta-silver-accent p {
        font-size: 1.2em;
    }
    .cta-image-placeholder {
        flex: 1;
        height: 300px; /* Fixed height for image container */
        max-width: 45%;
    }
    .cta-image-placeholder img {
        border-radius: 20px;
    }
    .cta-accent-line {
        display: none;
    }
}

@media (min-width: 1024px) {
    .cta-silver-accent {
        padding: 100px 60px;
    }
    .cta-container {
        padding: 50px;
    }
    .cta-silver-accent h2 {
        font-size: 4em;
    }
}
