css:
.faq-email-section {
    background-color: #2E2929;
    color: #FFFFFF;
    padding: 5rem 0;
    font-family: sans-serif;
}
.faq-email-section .container {
    width: 90%;
    max-width: 1100px;
    margin: 0 auto;
}
.faq-email-container {
    display: grid;
    grid-template-columns: 1fr;
    gap: 3rem;
    align-items: start;
}
@media (min-width: 992px) {
    .faq-email-container {
        grid-template-columns: 1fr 1fr;
    }
}
.faq-email-section .faq-side h2 {
    font-size: 2rem;
    font-weight: 600;
    margin-bottom: 2rem;
}
.faq-email-section .accordion {
    display: flex;
    flex-direction: column;
    gap: 0.5rem;
}
.faq-email-section .accordion-item {
    border-bottom: 1px solid #4B4343;
    transition: background-color 0.3s ease;
}
.faq-email-section .accordion-item[open] {
    background-color: #4B4343;
    border-radius: 8px;
    border-bottom: none;
}
.faq-email-section .accordion-item summary {
    padding: 1rem;
    cursor: pointer;
    list-style: none;
    display: flex;
    justify-content: space-between;
    align-items: center;
    font-weight: 500;
}
.faq-email-section .accordion-item summary::-webkit-details-marker {
    display: none;
}
.faq-email-section .accordion-item .icon {
    width: 12px;
    height: 12px;
    border-right: 2px solid #FFFFFF;
    border-bottom: 2px solid #FFFFFF;
    transform: rotate(45deg);
    transition: transform 0.3s ease;
    flex-shrink: 0;
}
.faq-email-section .accordion-item[open] summary .icon {
    transform: rotate(-135deg);
}
.faq-email-section .accordion-item p {
    padding: 0 1rem 1rem;
    color: #e0e0e0;
    font-size: 0.9rem;
    line-height: 1.6;
}

.faq-email-section .email-card {
    background-color: #FFFFFF;
    color: #000000;
    border-radius: 40px 40px 10px 10px;
    padding: 2.5rem;
}
.faq-email-section .email-card h2 {
    font-size: 2rem;
    font-weight: 600;
    margin-bottom: 1rem;
}
.faq-email-section .email-card p {
    color: #555;
    margin-bottom: 2rem;
    font-size: 0.9rem;
}
.faq-email-section .form-group {
    margin-bottom: 1.5rem;
}
.faq-email-section .form-group input,
.faq-email-section .form-group textarea {
    width: 100%;
    padding-bottom: 0.75rem;
    border: none;
    border-bottom: 1px solid #ccc;
    font-family: inherit;
    font-size: 1rem;
    background-color: transparent;
}
.faq-email-section .form-group input:focus,
.faq-email-section .form-group textarea:focus {
    outline: none;
    border-bottom-color: #4A82AA;
}
.faq-email-section .form-group textarea {
    resize: vertical;
}
.faq-email-section .btn-submit {
    background-color: #4A82AA;
    color: #FFFFFF;
    padding: 1rem 2rem;
    border: none;
    border-radius: 30px;
    font-weight: 600;
    cursor: pointer;
    width: 100%;
    transition: background-color 0.3s;
}
.faq-email-section .btn-submit:hover {
    background-color: #3a688a;
}
.faq-email-section .anim-delay-1 {
    transition-delay: 0.2s;
}
