css:
.faq-bild-rechts-section {
padding: 4rem 1rem;
background-color: #FDFBF6;
font-family: 'Inter', sans-serif;
}
.faq-bild-rechts-section .container {
max-width: 1100px;
width: 100%;
margin: 0 auto;
box-sizing: border-box;
}
.faq-bild-rechts-section .section-header {
text-align: center;
margin-bottom: 2.5rem;
padding: 0 0.5rem;
}
.faq-bild-rechts-section h2 {
font-size: clamp(1.75rem, 5vw, 3rem);
font-weight: 700;
color: var(--color-dark, #1F2937);
margin-bottom: 1rem;
line-height: 1.2;
}
.faq-bild-rechts-section .section-header p {
color: var(--color-secondary, #6B7280);
max-width: 42rem;
margin: 0 auto;
padding: 0 0.5rem;
line-height: 1.6;
}
.faq-bild-rechts-section .content-card {
background-color: var(--color-light, #FFFFFF);
border: 1px dotted #d1d5db;
padding: 1.5rem;
border-radius: 0.5rem;
}
@media (min-width: 768px) {
.faq-bild-rechts-section .content-card {
padding: 2.5rem;
}
}
.faq-bild-rechts-section .grid-container {
display: grid;
grid-template-columns: 1fr;
gap: 1.5rem;
align-items: start;
width: 100%;
box-sizing: border-box;
}
.faq-bild-rechts-section .image-container { order: 0; }
.faq-bild-rechts-section .accordion-container { order: 1; }
@media (min-width: 992px) {
.faq-bild-rechts-section .grid-container {
grid-template-columns: 1fr 1fr;
gap: 2.5rem;
align-items: center;
}
.faq-bild-rechts-section .accordion-container { order: 0; }
.faq-bild-rechts-section .image-container { order: 1; }
}
.faq-bild-rechts-section .image-container {
width: 100%;
}
.faq-bild-rechts-section .image-container img {
width: 100%;
max-width: 100%;
height: auto;
border-radius: 0.5rem;
object-fit: cover;
display: block;
}
.faq-bild-rechts-section .accordion-container {
display: flex;
flex-direction: column;
width: 100%;
gap: 0.5rem;
}
.faq-bild-rechts-section .faq-item {
border-bottom: 1px solid #e5e7eb;
}
.faq-bild-rechts-section .faq-item:last-child {
border-bottom: none;
}
.faq-bild-rechts-section .faq-item summary {
padding: 1.25rem 0.5rem;
cursor: pointer;
list-style: none;
display: flex;
align-items: center;
gap: 0.75rem;
font-size: 1.05rem;
font-weight: 600;
color: var(--color-dark, #1F2937);
transition: color 0.2s ease;
outline: none;
}
.faq-bild-rechts-section .faq-item summary:hover {
color: var(--color-primary, #3B82F6);
}
.faq-bild-rechts-section .faq-item[open] summary {
color: var(--color-primary, #3B82F6);
}
.faq-bild-rechts-section .faq-item summary::-webkit-details-marker {
display: none;
}
.faq-bild-rechts-section .faq-number {
color: var(--color-primary, #3B82F6);
flex-shrink: 0;
}
.faq-bild-rechts-section .faq-icon {
margin-left: auto;
width: 1rem;
height: 1rem;
transition: transform 0.3s ease;
flex-shrink: 0;
border-top: 2px solid var(--color-primary, #3B82F6);
border-right: 2px solid var(--color-primary, #3B82F6);
transform: rotate(135deg);
}
.faq-bild-rechts-section .faq-item[open] summary .faq-icon {
transform: rotate(-45deg);
}
.faq-bild-rechts-section .faq-answer {
padding: 0 0.5rem 1.25rem calc(1.1rem + 0.75rem);
line-height: 1.7;
}
.faq-bild-rechts-section .faq-answer p {
color: var(--color-secondary, #6B7280);
margin: 0;
}
@media (max-width: 991.98px) {
.faq-bild-rechts-section { padding: 3rem 1rem; }
.faq-bild-rechts-section .faq-item summary { padding: 1rem 0.5rem; }
.faq-bild-rechts-section .faq-answer { padding: 0 0.5rem 1rem calc(1.1rem + 0.75rem); }
}
*, *::before, *::after { box-sizing: border-box; }
html, body { width: 100%; max-width: 100%; overflow-x: hidden; }
.faq-bild-rechts-section .container,
.faq-bild-rechts-section .grid-container,
.faq-bild-rechts-section .accordion-container {
overflow-x: hidden;
}
