css:
.faq-bildsandblocks-section .top-part {
    background-color: var(--color-light, #FFFFFF);
    padding: 5rem 0;
}
.faq-bildsandblocks-section .bottom-part {
    background-color: #f3f4f6; /* gray-100 */
    padding: 5rem 0;
}
.faq-bildsandblocks-section .container {
    width: 90%;
    max-width: 1000px;
    margin: 0 auto;
}
.faq-bildsandblocks-section .top-grid {
    display: grid;
    grid-template-columns: 1fr;
    gap: 2rem;
    align-items: center;
}
@media (min-width: 768px) {
    .faq-bildsandblocks-section .top-grid {
        grid-template-columns: 1fr 1.5fr;
        gap: 3rem;
    }
}
.faq-bildsandblocks-section .header-content h2 {
    font-size: clamp(2.5rem, 5vw, 3.5rem);
    font-weight: 700;
    color: #3B82F6; /* A nice blue */
    margin-bottom: 1.5rem;
    line-height: 1.1;
}
.faq-bildsandblocks-section .header-content p {
    color: var(--color-secondary, #6B7280);
    margin-bottom: 0.5rem;
}
.faq-bildsandblocks-section .header-content .image-credit {
    font-size: 0.8rem;
    font-style: italic;
}
.faq-bildsandblocks-section .image-wrapper img {
    width: 100%;
    height: auto;
    border-radius: 0.5rem;
}
.faq-bildsandblocks-section .accordion-part {
    max-width: 600px;
    margin: 0 auto;
}
.faq-bildsandblocks-section .faq-item {
    border-bottom: 1px solid #e5e7eb;
}
.faq-bildsandblocks-section .faq-item[open] {
    border-bottom: none;
}
.faq-bildsandblocks-section .faq-item summary {
    padding: 1rem 0;
    cursor: pointer;
    list-style: none;
    display: flex;
    justify-content: space-between;
    align-items: center;
    font-weight: 500;
    color: #2EC5B6; /* A teal color */
    transition: background-color 0.2s;
}
.faq-bildsandblocks-section .faq-item summary::-webkit-details-marker {
    display: none;
}
.faq-bildsandblocks-section .faq-icon {
    font-size: 1.5rem;
    font-weight: 300;
    color: var(--color-secondary, #6B7280);
}
.faq-bildsandblocks-section .faq-item[open] > summary {
    background-color: #2EC5B6;
    color: var(--color-light, #FFFFFF);
    padding: 1rem;
    border-radius: 8px 8px 0 0;
}
.faq-bildsandblocks-section .faq-item[open] > summary .faq-icon {
    color: var(--color-light, #FFFFFF);
}
.faq-bildsandblocks-section .faq-answer {
    padding: 1.5rem;
    background-color: var(--color-light, #FFFFFF);
    border-radius: 0 0 8px 8px;
    box-shadow: 0 4px 15px rgba(0,0,0,0.08);
    margin-bottom: 1rem; /* Space after open item */
    border: 1px solid #e5e7eb;
    border-top: none;
}
.faq-bildsandblocks-section .faq-answer p {
    color: var(--color-dark, #1F2937);
    margin: 0;
}
.faq-bildsandblocks-section .anim-delay-1 { transition-delay: 0.1s; }
.faq-bildsandblocks-section .anim-delay-2 { transition-delay: 0.2s; }
