css:
.section-faq-box-border-simple-v1 {
  padding: 4rem 0;
  background-color: var(--color-light);
}
.section-faq-box-border-simple-v1 .container {
  max-width: 900px;
  margin: 0 auto;
  padding: 0 1rem;
}
.section-faq-box-border-simple-v1 .header {
  margin-bottom: 2rem;
  border-bottom: 2px solid var(--color-dark);
  padding-bottom: 1rem;
}
.section-faq-box-border-simple-v1 h2 {
  font-family: var(--font-heading);
  text-transform: uppercase;
  letter-spacing: 1px;
  font-size: 1.5rem;
  color: var(--color-dark);
}
.section-faq-box-border-simple-v1 .faq-item {
  border: 1px solid var(--color-dark);
  margin-bottom: -1px;
  background: #fff;
}
.section-faq-box-border-simple-v1 summary {
  list-style: none;
  padding: 1.25rem;
  display: flex;
  justify-content: space-between;
  align-items: center;
  cursor: pointer;
  font-weight: 700;
  color: var(--color-dark);
  background-color: #fff;
}
.section-faq-box-border-simple-v1 summary:hover {
  background-color: var(--color-surface-1);
}
.section-faq-box-border-simple-v1 summary::-webkit-details-marker {
  display: none;
}
.section-faq-box-border-simple-v1 .faq-icon {
  width: 14px;
  height: 14px;
  border: 1px solid var(--color-dark);
  background: var(--color-dark);
  transition: transform 0.3s ease;
}
.section-faq-box-border-simple-v1 details[open] .faq-icon {
  transform: rotate(45deg);
  background: transparent;
}
.section-faq-box-border-simple-v1 .faq-answer {
  padding: 0 1.25rem 1.25rem 1.25rem;
  color: var(--color-secondary);
  border-top: 1px solid var(--color-border);
}
