css:
.faq-calm-section {
    padding: 5rem 0;
    background-color: var(--color-light, #FFFFFF);
    color: var(--color-dark, #1F2937);
    font-family: 'Inter', sans-serif;
}
.faq-calm-section .container {
    width: 90%;
    max-width: 800px;
    margin: 0 auto;
}
.faq-calm-section .section-header {
    text-align: center;
    margin-bottom: 3rem;
}
.faq-calm-section .pre-title {
    font-size: 0.9rem;
    font-weight: 600;
    color: var(--color-secondary, #6B7280);
    text-transform: uppercase;
    letter-spacing: 1px;
    margin-bottom: 0.5rem;
}
.faq-calm-section h2 {
    font-family: 'Poppins', sans-serif;
    font-size: clamp(2.5rem, 5vw, 3rem);
    font-weight: 800;
}
.faq-calm-section .faq-accordion {
    display: flex;
    flex-direction: column;
    gap: 0;
}
.faq-calm-section .faq-item {
    border-bottom: 1px solid #e5e7eb; /* gray-200 */
}
.faq-calm-section .faq-item:first-of-type {
    border-top: 1px solid #e5e7eb; /* gray-200 */
}
.faq-calm-section .faq-item summary {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 1.5rem 1rem;
    cursor: pointer;
    list-style: none;
    font-weight: 600;
    transition: background-color 0.3s ease;
}
.faq-calm-section .faq-item summary::-webkit-details-marker {
    display: none;
}
.faq-calm-section .faq-icon {
    flex-shrink: 0;
    margin-left: 1rem;
    transition: transform 0.3s ease;
    color: #79F0E0;
}
.faq-calm-section .faq-item[open] > summary {
    background-color: #79F0E0; /* Light turquoise */
    color: var(--color-dark, #1F2937);
}
.faq-calm-section .faq-item[open] > summary .faq-icon {
    transform: rotate(180deg);
    color: var(--color-dark, #1F2937);
}
.faq-calm-section .faq-content {
    display: grid;
    grid-template-rows: 0fr;
    transition: grid-template-rows 0.3s ease-out;
}
.faq-calm-section .faq-item[open] .faq-content {
    grid-template-rows: 1fr;
}
.faq-calm-section .faq-content p {
    overflow: hidden;
    padding: 0 1rem 1.5rem 1rem;
    color: var(--color-secondary, #6B7280);
    margin: 0;
    line-height: 1.7;
    max-width: 90%;
}
.faq-calm-section .anim-delay-1 {
    transition-delay: 0.2s;
}
