css:
.section-faq-card-layout-h6q .container {
    max-width: 1000px;
    margin: 0 auto;
    padding: 50px 20px;
}

.section-faq-card-layout-h6q .faq-card-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
    gap: 30px;
}

.section-faq-card-layout-h6q .faq-card {
    background-color: var(--color-surface-1);
    border-radius: 12px;
    box-shadow: 0 4px 15px rgba(0, 0, 0, 0.05);
    overflow: hidden;
    padding: 30px;
    transition: transform 0.3s ease, box-shadow 0.3s ease;
}

.section-faq-card-layout-h6q .faq-card:hover {
    transform: translateY(-5px);
    box-shadow: 0 8px 25px rgba(0, 0, 0, 0.1);
}

.section-faq-card-layout-h6q .faq-card-header {
    margin-bottom: 20px;
}

.section-faq-card-layout-h6q .faq-card-title {
    font-family: var(--font-heading);
    font-size: 1.4rem;
    color: var(--color-dark);
    margin: 0;
}

.section-faq-card-layout-h6q .faq-card-body {
    font-family: var(--font-body);
    font-size: 1rem;
    color: var(--color-secondary);
    line-height: 1.7;
}

.section-faq-card-layout-h6q .faq-card-body p {
    margin: 0;
}
