css:
.faq-categorized-section {
    padding: 5rem 0;
    background-color: var(--color-light, #FFFFFF);
}
.faq-categorized-section .container {
    width: 90%;
    max-width: 1100px;
    margin: 0 auto;
}
.faq-categorized-container {
    display: grid;
    grid-template-columns: 1fr;
    gap: 3rem;
    align-items: flex-start;
}
@media (min-width: 992px) {
    .faq-categorized-container {
        grid-template-columns: 1fr 1.5fr;
    }
}
.faq-categorized-section .faq-header h2 {
    font-size: clamp(3rem, 6vw, 4rem);
    font-weight: 700;
    line-height: 1.1;
    margin-bottom: 1.5rem;
    color: var(--color-dark, #1F2937);
}
.faq-categorized-section .faq-header p {
    color: var(--color-secondary, #6B7280);
    font-size: 1.1rem;
}
.faq-categorized-section .faq-accordion-wrapper {
    display: flex;
    flex-direction: column;
    gap: 2.5rem;
}
.faq-categorized-section .category-tag {
    display: inline-block;
    background-color: #e5e7eb; /* gray-200 */
    color: var(--color-secondary, #6B7280);
    padding: 0.25rem 0.75rem;
    border-radius: 9999px;
    font-size: 0.75rem;
    font-weight: 600;
    margin-bottom: 1.5rem;
}
.faq-categorized-section .faq-item {
    border-bottom: 1px solid #e5e7eb;
}
.faq-categorized-section .faq-item summary {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 1rem 0;
    cursor: pointer;
    list-style: none;
    font-weight: 600;
}
.faq-categorized-section .faq-item summary::-webkit-details-marker { display: none; }
.faq-categorized-section .faq-icon {
    width: 28px;
    height: 28px;
    border-radius: 50%;
    background-color: #f3f4f6;
    color: #6b7280;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 1.5rem;
    line-height: 1;
    transition: transform 0.3s;
    flex-shrink: 0;
}
.faq-categorized-section .faq-item[open] > summary .faq-icon {
    transform: rotate(45deg);
}
.faq-categorized-section .faq-item p {
    padding: 0 0 1rem 0;
    color: var(--color-secondary, #6B7280);
    margin: 0;
}
