css:
.faq-centered-categorized-section {
    padding: 5rem 0;
    background-color: var(--color-light, #FFFFFF);
}
.faq-centered-categorized-section .container {
    width: 90%;
    max-width: 800px;
    margin: 0 auto;
}
.faq-centered-categorized-section .section-header {
    text-align: center;
    margin-bottom: 4rem;
}
.faq-centered-categorized-section h2 {
    font-size: clamp(2.5rem, 5vw, 3rem);
    font-weight: 700;
    color: var(--color-dark, #1F2937);
    margin-bottom: 1rem;
}
.faq-centered-categorized-section .section-header p {
    color: var(--color-secondary, #6B7280);
    max-width: 500px;
    margin: 0 auto;
}
.faq-centered-categorized-section .faq-list-wrapper {
    display: flex;
    flex-direction: column;
    gap: 3rem;
}
.faq-centered-categorized-section .faq-category h3 {
    font-size: 1.5rem;
    font-weight: 600;
    color: var(--color-dark, #1F2937);
    margin-bottom: 1.5rem;
    padding-bottom: 1rem;
    border-bottom: 1px solid #e5e7eb;
}
.faq-centered-categorized-section .faq-item {
    border-bottom: 1px solid #e5e7eb;
}
.faq-centered-categorized-section .faq-item:last-child {
    border-bottom: none;
}
.faq-centered-categorized-section .faq-item summary {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 1.25rem 0;
    cursor: pointer;
    list-style: none;
    font-weight: 600;
}
.faq-centered-categorized-section .faq-item summary::-webkit-details-marker { display: none; }
.faq-centered-categorized-section .faq-icon {
    width: 28px;
    height: 28px;
    border-radius: 8px;
    background-color: #f3f4f6;
    color: #6b7280;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 1.5rem;
    line-height: 1;
    transition: transform 0.3s;
    flex-shrink: 0;
}
.faq-centered-categorized-section .faq-item[open] > summary .faq-icon {
    transform: rotate(45deg);
}
.faq-centered-categorized-section .faq-item p {
    padding-bottom: 1.25rem;
    color: var(--color-secondary, #6B7280);
    margin: 0;
}
