css:
.faq-chic-section {
    padding: clamp(3rem, 6vw, 5rem) 0; /* Fluid padding */
    background-color: #FDFBF8; /* Soft cream */
    font-family: sans-serif; /* Defaults to Inter from index.html */
}
.faq-chic-section .container {
    width: 90%;
    max-width: 1100px;
    margin: 0 auto;
    padding: 0 1rem;
}
.faq-chic-section .faq-grid {
    display: grid;
    grid-template-columns: 1fr;
    gap: 2rem;
}
.faq-chic-section .image-container {
    order: -1; /* Image on top on mobile */
}

@media (min-width: 768px) {
    .faq-chic-section {
        padding: clamp(4rem, 8vw, 6rem) 0;
    }
}
@media (min-width: 992px) {
    .faq-chic-section .faq-grid {
        grid-template-columns: 1fr 1fr;
        gap: 4rem;
        align-items: start; /* Prevents image from moving vertically */
    }
    .faq-chic-section .image-container {
        order: 0; /* Reset order for desktop */
    }
}
.faq-chic-section .image-container img {
    width: 100%;
    height: 100%;
    border-radius: 1rem;
    object-fit: cover;
    aspect-ratio: 4 / 5;
    min-height: 400px;
}
.faq-chic-section h2 {
    font-family: serif;
    font-size: clamp(2rem, 4vw, 2.5rem);
    font-weight: 700;
    color: #4A4A4A; /* Dark gray */
    margin-bottom: 2rem;
}
.faq-chic-section .faq-item {
    border-bottom: 1px solid #DCDCDC;
}
.faq-chic-section .faq-item summary {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 1.5rem 0;
    cursor: pointer;
    list-style: none;
    font-weight: 600;
    color: #4A4A4A;
}
.faq-chic-section .faq-item summary::-webkit-details-marker { display: none; }
.faq-chic-section .faq-item .icon {
    font-size: 1.5rem;
    color: #BEA7A2; /* Muted rose */
    transition: transform 0.3s ease;
    font-style: normal;
}
.faq-chic-section .faq-item[open] summary .icon {
    transform: rotate(45deg);
}
.faq-chic-section .faq-item p {
    padding: 0 0 1.5rem;
    color: #707070; /* Medium gray */
    margin: 0;
    line-height: 1.7;
}
.faq-chic-section .anim-delay-1 { transition-delay: 0.2s; }
