css:
.faq-circle-pictures-section {
    padding: 5rem 0;
    background-color: var(--color-light, #FFFFFF);
    color: var(--color-dark, #1F2937);
    font-family: sans-serif;
}
.faq-circle-pictures-section .container {
    width: 90%;
    max-width: 800px;
    margin: 0 auto;
}
.faq-circle-pictures-section .section-title {
    text-align: center;
    font-size: 2.5rem;
    font-weight: 700;
    margin-bottom: 2rem;
}
.faq-circle-pictures-section .team-intro {
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 1.5rem;
    margin-bottom: 3rem;
}
.faq-circle-pictures-section .avatars {
    display: flex;
}
.faq-circle-pictures-section .avatars img {
    width: 80px;
    height: 80px;
    border-radius: 50%;
    object-fit: cover;
    border: 4px solid var(--color-light, #FFFFFF);
    box-shadow: 0 4px 10px rgba(0,0,0,0.1);
}
.faq-circle-pictures-section .avatars img:not(:first-child) {
    margin-left: -20px;
}
.faq-circle-pictures-section .sample-text {
    font-size: 0.9rem;
    color: var(--color-secondary, #6B7280);
    max-width: 300px;
    text-align: center;
}
.faq-circle-pictures-section .faq-accordion {
    display: flex;
    flex-direction: column;
    gap: 1rem;
}
.faq-circle-pictures-section .faq-item {
    border-bottom: 1px solid #e5e7eb;
}
.faq-circle-pictures-section .faq-item summary {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 1.5rem 0.5rem;
    cursor: pointer;
    font-weight: 600;
    list-style: none; /* Remove default marker */
}
.faq-circle-pictures-section .faq-item summary::-webkit-details-marker {
    display: none;
}
.faq-circle-pictures-section .faq-icon {
    transition: transform 0.3s ease;
    font-size: 1.5rem;
    font-weight: 300;
    color: #2EC5B6;
}
.faq-circle-pictures-section .faq-item[open] > summary {
    color: #2EC5B6;
}
.faq-circle-pictures-section .faq-item[open] > summary .faq-icon {
    transform: rotate(45deg);
}
.faq-circle-pictures-section .faq-answer {
    padding: 0 0.5rem 1.5rem;
}
.faq-circle-pictures-section .faq-answer p {
    color: var(--color-secondary, #6B7280);
    line-height: 1.6;
    margin: 0;
}
.faq-circle-pictures-section .anim-delay-1 { transition-delay: 0.1s; }
.faq-circle-pictures-section .anim-delay-2 { transition-delay: 0.2s; }
