css:
.section-faq-collapsible-panels-a3d .container {
    max-width: 850px;
    margin: 0 auto;
    padding: 50px 20px;
}

.section-faq-collapsible-panels-a3d .panels-container {
    background-color: var(--color-surface-1);
    border-radius: 12px;
    box-shadow: 0 5px 20px rgba(0, 0, 0, 0.08);
    overflow: hidden;
}

.section-faq-collapsible-panels-a3d .faq-panel-item {
    border-bottom: 1px solid var(--color-border);
}

.section-faq-collapsible-panels-a3d .faq-panel-item:last-child {
    border-bottom: none;
}

.section-faq-collapsible-panels-a3d .faq-panel-item summary {
    display: flex;
    justify-content: space-between;
    align-items: center;
    cursor: pointer;
    padding: 25px;
    font-family: var(--font-body);
    color: var(--color-dark);
    font-size: 1.2rem;
    font-weight: 600;
    transition: color 0.3s ease;
}

.section-faq-collapsible-panels-a3d .faq-panel-item[open] summary {
    background-color: var(--color-primary);
    color: var(--color-light);
}

.section-faq-collapsible-panels-a3d .faq-panel-item summary .panel-title {
    flex-grow: 1;
    margin-right: 15px;
}

.section-faq-collapsible-panels-a3d .faq-panel-item summary .panel-icon {
    transition: transform 0.3s ease;
    font-size: 1em;
    color: var(--color-secondary);
}

.section-faq-collapsible-panels-a3d .faq-panel-item[open] summary .panel-icon {
    transform: rotate(180deg);
    color: var(--color-light);
}

.section-faq-collapsible-panels-a3d .panel-content {
    padding: 0 25px 25px;
    color: var(--color-dark);
    font-family: var(--font-body);
    font-size: 1rem;
    line-height: 1.7;
    background-color: var(--color-light);
}

.section-faq-collapsible-panels-a3d .panel-content p {
    margin: 0;
}

/* Ensure native markers are hidden */
.section-faq-collapsible-panels-a3d summary::-webkit-details-marker {
    display: none;
}
.section-faq-collapsible-panels-a3d summary {
    list-style: none;
}
