css:
.section-faq-colored-block-v1 {
  padding: 0;
  background-color: var(--color-light);
}
.section-faq-colored-block-v1 .container {
  max-width: 100%;
  padding: 0;
}
.section-faq-colored-block-v1 .grid {
  display: grid;
  grid-template-columns: 1fr;
}
@media (min-width: 992px) {
  .section-faq-colored-block-v1 .grid {
    grid-template-columns: 40% 60%;
  }
}
.section-faq-colored-block-v1 .text-block {
  background-color: var(--color-primary);
  color: #fff;
  padding: 5rem 3rem;
  display: flex;
  flex-direction: column;
  justify-content: center;
}
.section-faq-colored-block-v1 h2 {
  font-family: var(--font-heading);
  font-size: 2.5rem;
  margin-bottom: 1.5rem;
  color: #fff;
}
.section-faq-colored-block-v1 .accordion-block {
  padding: 5rem 3rem;
  background-color: #fff;
}
.section-faq-colored-block-v1 .faq-item {
  border-bottom: 1px solid var(--color-border);
  margin-bottom: 1rem;
}
.section-faq-colored-block-v1 summary {
  list-style: none;
  padding: 1rem 0;
  display: flex;
  justify-content: space-between;
  align-items: center;
  cursor: pointer;
  font-weight: 600;
  color: var(--color-dark);
}
.section-faq-colored-block-v1 summary::-webkit-details-marker {
  display: none;
}
.section-faq-colored-block-v1 .faq-icon {
  width: 12px;
  height: 12px;
  border-right: 2px solid var(--color-primary);
  border-bottom: 2px solid var(--color-primary);
  transform: rotate(45deg);
  transition: transform 0.3s;
}
.section-faq-colored-block-v1 details[open] .faq-icon {
  transform: rotate(225deg);
}
.section-faq-colored-block-v1 .faq-answer {
  padding-bottom: 1rem;
  color: var(--color-secondary);
}
