css:
.section-faq-dark-theme-r7s8 .container { max-width: 700px; margin: 0 auto; padding: 40px 20px; }
.section-faq-dark-theme-r7s8 .accordion-container {}
.section-faq-dark-theme-r7s8 .faq-item {
    border-bottom: 1px solid rgba(255, 255, 255, 0.1);
}
.section-faq-dark-theme-r7s8 .faq-item:last-child {
    border-bottom: none;
}
.section-faq-dark-theme-r7s8 summary {
    list-style: none;
    -webkit-appearance: none;
    appearance: none;
    padding: 20px;
    display: flex;
    justify-content: space-between;
    align-items: center;
    cursor: pointer;
    background-color: var(--color-dark);
    transition: background-color 0.3s ease;
}
.section-faq-dark-theme-r7s8 summary:hover {
    background-color: rgba(255, 255, 255, 0.05);
}
.section-faq-dark-theme-r7s8 summary::-webkit-details-marker {
    display: none;
}
.section-faq-dark-theme-r7s8 .faq-question {
    flex-grow: 1;
    font-weight: 500;
    color: var(--color-light);
    font-size: 1.1em;
    margin-right: 15px;
}
.section-faq-dark-theme-r7s8 .faq-icon {
    width: 20px;
    height: 20px;
    position: relative;
    transition: transform 0.3s ease;
    flex-shrink: 0;
}
.section-faq-dark-theme-r7s8 .faq-icon::before,
.section-faq-dark-theme-r7s8 .faq-icon::after {
    content: '';
    position: absolute;
    background-color: var(--color-primary);
}
.section-faq-dark-theme-r7s8 .faq-icon::before {
    top: 50%;
    left: 0;
    width: 20px;
    height: 2px;
    transform: translateY(-50%);
}
.section-faq-dark-theme-r7s8 .faq-icon::after {
    top: 0;
    left: 50%;
    width: 2px;
    height: 20px;
    transform: translateX(-50%);
}
.section-faq-dark-theme-r7s8 details[open] summary .faq-icon {
    transform: rotate(45deg);
}
.section-faq-dark-theme-r7s8 details[open] summary .faq-icon::after {
    opacity: 0;
}
.section-faq-dark-theme-r7s8 .faq-answer {
    background-color: var(--color-dark);
    color: var(--color-secondary);
    padding: 20px;
    border-top: 1px solid rgba(255, 255, 255, 0.1);
}
.section-faq-dark-theme-r7s8 .faq-answer p {
    margin: 0;
    line-height: 1.6;
    font-size: 1em;
}
@media (max-width: 768px) {
    .section-faq-dark-theme-r7s8 .faq-question {
        font-size: 1em;
    }
}
