css:
.faq-fakten-fragen-section {
    position: relative;
    padding: 5rem 0;
    background-color: var(--color-dark, #000000);
    color: var(--color-light, #FFFFFF);
    overflow: hidden;
}
.faq-fakten-fragen-section .background-image-wrapper {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    z-index: 0;
    opacity: 0.3;
}
.faq-fakten-fragen-section .background-image-wrapper img {
    width: 100%;
    height: 100%;
    object-fit: cover;
}
.faq-fakten-fragen-section .container {
    width: 90%;
    max-width: 1100px;
    margin: 0 auto;
    position: relative;
    z-index: 1;
}
.faq-fakten-fragen-section .faq-grid {
    display: grid;
    grid-template-columns: 1fr;
    gap: 3rem;
    align-items: start;
}
@media (min-width: 992px) {
    .faq-fakten-fragen-section .faq-grid {
        grid-template-columns: 1fr 1fr;
    }
}
.faq-fakten-fragen-section .content-side h2 {
    font-size: clamp(2.5rem, 5vw, 3.5rem);
    font-weight: 700;
    margin-bottom: 1.5rem;
}
.faq-fakten-fragen-section .content-side p {
    color: #e5e7eb; /* gray-200 */
    line-height: 1.7;
    margin-bottom: 1.5rem;
    max-width: 450px;
}
.faq-fakten-fragen-section .accordion-side {
    display: flex;
    flex-direction: column;
}
.faq-fakten-fragen-section .faq-item {
    background-color: transparent;
    border-bottom: 1px solid rgba(255, 255, 255, 0.2);
}
.faq-fakten-fragen-section .faq-item[open] {
    background-color: var(--color-light, #FFFFFF);
    color: var(--color-dark, #1F2937);
    border-radius: 8px;
    border-bottom: none;
    margin-bottom: 1rem;
}
.faq-fakten-fragen-section .faq-item summary {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 1.5rem 1rem;
    cursor: pointer;
    list-style: none;
    font-weight: 600;
    font-size: 1.1rem;
}
.faq-fakten-fragen-section .faq-item summary::-webkit-details-marker {
    display: none;
}
.faq-fakten-fragen-section .faq-icon {
    font-size: 1.5rem;
    transition: transform 0.3s;
    font-weight: bold;
}
.faq-fakten-fragen-section .faq-item[open] > summary .faq-icon {
    transform: rotate(45deg);
    color: var(--color-dark, #1F2937);
}
.faq-fakten-fragen-section .faq-answer {
    padding: 0 1rem 1.5rem;
}
.faq-fakten-fragen-section .faq-answer p {
    color: var(--color-secondary, #6B7280);
    line-height: 1.6;
    margin: 0;
}
.faq-fakten-fragen-section .anim-delay-1 { transition-delay: 0.1s; }
.faq-fakten-fragen-section .anim-delay-2 { transition-delay: 0.2s; }
