css:
.section-faq-glassmorphism-v1 {
  position: relative;
  padding: 6rem 0;
  overflow: hidden;
  color: #fff;
}
.section-faq-glassmorphism-v1 .bg-image {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  z-index: -1;
}
.section-faq-glassmorphism-v1 .bg-img {
  width: 100%;
  height: 100%;
  object-fit: cover;
}
.section-faq-glassmorphism-v1 .container {
  max-width: 800px;
  margin: 0 auto;
  padding: 0 1.5rem;
  position: relative;
  z-index: 1;
}
.section-faq-glassmorphism-v1 h2 {
  text-align: center;
  margin-bottom: 3rem;
  font-family: var(--font-heading);
  font-size: 2.5rem;
  text-shadow: 0 2px 4px rgba(0,0,0,0.2);
}
.section-faq-glassmorphism-v1 .faq-item {
  background: rgba(255, 255, 255, 0.1);
  backdrop-filter: blur(10px);
  -webkit-backdrop-filter: blur(10px);
  border: 1px solid rgba(255, 255, 255, 0.2);
  border-radius: 15px;
  margin-bottom: 1rem;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
}
.section-faq-glassmorphism-v1 summary {
  list-style: none;
  padding: 1.5rem;
  display: flex;
  justify-content: space-between;
  align-items: center;
  cursor: pointer;
  font-weight: 600;
  font-family: var(--font-body);
}
.section-faq-glassmorphism-v1 summary::-webkit-details-marker {
  display: none;
}
.section-faq-glassmorphism-v1 .faq-icon {
  font-size: 0.8rem;
  transition: transform 0.3s ease;
}
.section-faq-glassmorphism-v1 details[open] .faq-icon {
  transform: rotate(180deg);
}
.section-faq-glassmorphism-v1 .faq-answer {
  padding: 0 1.5rem 1.5rem 1.5rem;
  line-height: 1.6;
  font-size: 0.95rem;
  opacity: 0.9;
}
