css:
.faq-gradient-accent-section {
    width: 100%;
    max-width: 100vw;
    overflow-x: hidden;
    padding: 4rem 1rem;
    box-sizing: border-box;
}

.faq-gradient-accent-section * {
    box-sizing: border-box;
}

.faq-gradient-accent-section .container {
    width: 100%;
    max-width: 900px;
    margin: 0 auto;
    padding: 0 1rem;
}

.section-header {
    text-align: center;
    margin-bottom: 3rem;
}

.section-header h2 {
    font-size: clamp(2rem, 6vw, 3rem);
    margin: 0 0 1rem;
    line-height: 1.2;
    word-wrap: break-word;
}

.gradient-text {
    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
}

.section-header p {
    font-size: clamp(1rem, 3.5vw, 1.1rem);
    color: #666;
    margin: 0;
    padding: 0 1rem;
    word-wrap: break-word;
}

.faq-accordion-wrapper {
    margin: 2rem 0;
}

.faq-item {
    background: #fff;
    border: 1px solid #e0e0e0;
    border-radius: 8px;
    margin-bottom: 1rem;
    overflow: hidden;
}

.faq-item summary {
    padding: 1rem 1.5rem;
    cursor: pointer;
    list-style: none;
    position: relative;
    font-weight: 600;
    font-size: clamp(1rem, 4vw, 1.1rem);
    line-height: 1.4;
    word-wrap: break-word;
    display: flex;
    align-items: center;
    gap: 1rem;
}

.faq-item summary::-webkit-details-marker {
    display: none;
}

.faq-icon {
    margin-left: auto;
    font-size: 1.5rem;
    flex-shrink: 0;
    width: 24px;
    text-align: center;
    transition: transform 0.3s ease;
}

.faq-item[open] summary .faq-icon {
    transform: rotate(45deg);
}

.faq-content {
    padding: 0 1.5rem 1.5rem;
}

.faq-content p {
    margin: 0;
    font-size: clamp(0.9rem, 3.5vw, 1rem);
    line-height: 1.6;
    color: #555;
    word-wrap: break-word;
    overflow-wrap: break-word;
}

.faq-content a {
    color: #667eea;
    word-break: break-all;
}

/* Animations */
@media (prefers-reduced-motion: no-preference) {
    .anim-fade-in-up {
        opacity: 0;
        transform: translateY(20px);
        animation: fadeInUp 0.6s ease-out forwards;
    }

    .anim-delay-1 {
        animation-delay: 0.2s;
    }

    @keyframes fadeInUp {
        from {
            opacity: 0;
            transform: translateY(20px);
        }
        to {
            opacity: 1;
            transform: translateY(0);
        }
    }
}
