css:
.section-faq-gradient-header-g7h8 .container { max-width: 800px; margin: 0 auto; padding: 40px 20px; }
.section-faq-gradient-header-g7h8 .accordion-container {}
.section-faq-gradient-header-g7h8 .faq-item {
    border-bottom: 1px solid var(--color-border);
}
.section-faq-gradient-header-g7h8 .faq-item:last-child {
    border-bottom: none;
}
.section-faq-gradient-header-g7h8 summary {
    list-style: none;
    -webkit-appearance: none;
    appearance: none;
    padding: 20px;
    display: flex;
    justify-content: space-between;
    align-items: center;
    cursor: pointer;
    background: var(--color-primary);
    background: linear-gradient(90deg, var(--color-primary) 0%, var(--color-secondary) 100%);
    transition: background 0.3s ease;
}
.section-faq-gradient-header-g7h8 summary:hover {
    background: linear-gradient(90deg, var(--color-secondary) 0%, var(--color-primary) 100%);
}
.section-faq-gradient-header-g7h8 summary::-webkit-details-marker {
    display: none;
}
.section-faq-gradient-header-g7h8 .faq-number {
    font-weight: bold;
    color: var(--color-light);
    margin-right: 15px;
    font-size: 1.1em;
    text-shadow: 1px 1px 2px rgba(0,0,0,0.2);
}
.section-faq-gradient-header-g7h8 .faq-question {
    flex-grow: 1;
    font-weight: 600;
    color: var(--color-light);
    font-size: 1.15em;
    margin-right: 15px;
    text-shadow: 1px 1px 2px rgba(0,0,0,0.2);
}
.section-faq-gradient-header-g7h8 .faq-icon {
    width: 20px;
    height: 20px;
    position: relative;
    transition: transform 0.3s ease;
}
.section-faq-gradient-header-g7h8 .faq-icon::before,
.section-faq-gradient-header-g7h8 .faq-icon::after {
    content: '';
    position: absolute;
    background-color: var(--color-light);
    border-radius: 2px;
}
.section-faq-gradient-header-g7h8 .faq-icon::before {
    top: 50%;
    left: 2px;
    width: 16px;
    height: 4px;
    transform: translateY(-50%);
}
.section-faq-gradient-header-g7h8 .faq-icon::after {
    top: 2px;
    left: 50%;
    width: 4px;
    height: 16px;
    transform: translateX(-50%);
}
.section-faq-gradient-header-g7h8 details[open] summary .faq-icon {
    transform: rotate(45deg);
}
.section-faq-gradient-header-g7h8 details[open] summary .faq-icon::after {
    opacity: 0;
}
.section-faq-gradient-header-g7h8 .faq-answer {
    background-color: var(--color-dark);
    color: var(--color-secondary);
    padding: 25px;
    border-top: 1px solid var(--color-border);
}
.section-faq-gradient-header-g7h8 .faq-answer p {
    margin: 0;
    line-height: 1.6;
    font-size: 1em;
}
@media (max-width: 768px) {
    .section-faq-gradient-header-g7h8 .faq-question {
        font-size: 1.05em;
    }
}
