css:
.section-faq-grid-2col-v1 {
  padding: 6rem 0;
  background-color: var(--color-light);
}
.section-faq-grid-2col-v1 .container {
  max-width: 1100px;
  margin: 0 auto;
  padding: 0 1rem;
}
.section-faq-grid-2col-v1 .header {
  text-align: center;
  margin-bottom: 3rem;
}
.section-faq-grid-2col-v1 h2 {
  font-family: var(--font-heading);
  font-size: 2.2rem;
  color: var(--color-dark);
}
.section-faq-grid-2col-v1 .accordion-grid {
  display: grid;
  grid-template-columns: 1fr;
  gap: 1.5rem;
  align-items: start;
}
@media (min-width: 768px) {
  .section-faq-grid-2col-v1 .accordion-grid {
    grid-template-columns: 1fr 1fr;
  }
}
.section-faq-grid-2col-v1 .faq-item {
  background: #fff;
  border: 1px solid var(--color-border);
  border-radius: 8px;
  padding: 0.5rem;
}
.section-faq-grid-2col-v1 summary {
  list-style: none;
  padding: 1rem;
  display: flex;
  justify-content: space-between;
  align-items: center;
  cursor: pointer;
  font-weight: 600;
  color: var(--color-dark);
  font-family: var(--font-body);
}
.section-faq-grid-2col-v1 summary::-webkit-details-marker {
  display: none;
}
.section-faq-grid-2col-v1 .faq-toggle {
  width: 10px;
  height: 10px;
  border-right: 2px solid var(--color-secondary);
  border-bottom: 2px solid var(--color-secondary);
  transform: rotate(45deg);
  transition: transform 0.2s;
}
.section-faq-grid-2col-v1 details[open] .faq-toggle {
  transform: rotate(225deg);
  border-color: var(--color-primary);
}
.section-faq-grid-2col-v1 .faq-answer {
  padding: 0 1rem 1rem 1rem;
  color: var(--color-secondary);
  font-size: 0.9rem;
}
