css:
.faq-grid-section {
    padding: 5rem 0;
    background-color: var(--color-light, #FFFFFF);
    color: var(--color-dark, #1F2937);
}
.faq-grid-section .container {
    width: 90%;
    max-width: 1000px;
    margin: 0 auto;
}
.faq-grid-section .section-header {
    text-align: center;
    margin-bottom: 3rem;
}
.faq-grid-section h2 {
    font-size: clamp(2.5rem, 5vw, 3rem);
    font-weight: 700;
    margin-bottom: 1rem;
}
.faq-grid-section .section-header p {
    color: var(--color-secondary, #6B7280);
    max-width: 500px;
    margin: 0 auto;
}
.faq-grid-section .faq-grid {
    display: grid;
    grid-template-columns: 1fr;
    gap: 1.5rem;
}
@media (min-width: 768px) {
    .faq-grid-section .faq-grid {
        grid-template-columns: 1fr 1fr;
    }
}
.faq-grid-section .faq-card {
    background-color: #f3f4f6; /* gray-100 */
    border-radius: 1rem;
    padding: 2rem;
    transition: background-color 0.3s;
}
.faq-grid-section .faq-card[open] {
    background-color: #e5e7eb; /* gray-200 */
}
.faq-grid-section .faq-card summary {
    font-size: 1.2rem;
    font-weight: 600;
    margin-bottom: 1rem;
    list-style: none;
    cursor: pointer;
    position: relative;
    padding-right: 2rem;
}
.faq-grid-section .faq-card summary::-webkit-details-marker { display: none; }
.faq-grid-section .faq-card summary::after {
    content: '↓';
    position: absolute;
    right: 0;
    top: 50%;
    transform: translateY(-50%) rotate(0deg);
    transition: transform 0.3s;
    background-color: #e5e7eb;
    color: var(--color-secondary, #6B7280);
    border-radius: 50%;
    width: 24px;
    height: 24px;
    display: flex;
    align-items: center;
    justify-content: center;
}
.faq-grid-section .faq-card[open] summary::after {
    transform: translateY(-50%) rotate(180deg);
}
.faq-grid-section .faq-card p {
    color: var(--color-secondary, #6B7280);
    line-height: 1.6;
    margin: 0;
}
.faq-grid-section .anim-delay-1 { transition-delay: 0.1s; }
