css:
.faq-image-header-section {
    background-color: var(--color-light, #FFFFFF);
    font-family: 'Inter', sans-serif;
}
.faq-image-header-section .faq-header {
    position: relative;
    padding: 5rem 1.5rem;
    text-align: center;
    color: #fff;
    overflow: hidden;
}
.faq-image-header-section .header-bg {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    object-fit: cover;
    z-index: 0;
}
.faq-image-header-section .header-overlay {
    position: absolute;
    inset: 0;
    background-color: rgba(0,0,0,0.5);
    z-index: 1;
}
.faq-image-header-section .faq-header .container {
    position: relative;
    z-index: 2;
    max-width: 900px;
    margin: 0 auto;
}
.faq-image-header-section .faq-header h2 {
    font-size: clamp(2.5rem, 5vw, 3.5rem);
    font-weight: 700;
}
.faq-image-header-section .faq-body {
    padding: 4rem 1rem;
}
.faq-image-header-section .faq-body .container {
    max-width: 900px;
    margin: 0 auto;
}
.faq-image-header-section .faq-accordion {
    display: flex;
    flex-direction: column;
    gap: 1rem;
}
.faq-image-header-section .faq-item {
    background-color: #f8f9fa;
    border-radius: 8px;
}
.faq-image-header-section .faq-item summary {
    display: flex;
    justify-content: space-between;
    align-items: center;
    gap: 1rem;
    padding: 1.5rem;
    font-weight: 500;
    color: var(--color-dark, #2C3E50);
    cursor: pointer;
    list-style: none;
}
.faq-image-header-section .faq-item summary > span:first-child {
    flex: 1;
    min-width: 0; /* Allows text to wrap */
}
.faq-image-header-section .faq-item summary::-webkit-details-marker { display: none; }
.faq-image-header-section .faq-icon {
    transition: transform 0.3s ease;
    color: var(--color-secondary, #576574);
    flex-shrink: 0;
}
.faq-image-header-section .faq-item[open] summary .faq-icon {
    transform: rotate(180deg);
}
.faq-image-header-section .faq-content {
    padding: 0 1.5rem 1.5rem 1.5rem;
    color: var(--color-secondary, #576574);
    line-height: 1.6;
}
