css:
.faq-image-overlap-section {
    padding: 5rem 0;
    background-color: var(--color-light, #FFFFFF);
}
.faq-image-overlap-section .container {
    width: 90%;
    max-width: 1000px;
    margin: 0 auto;
}
.faq-overlap-container {
    display: grid;
    grid-template-columns: 1fr;
    gap: 2rem;
}
@media (min-width: 768px) {
    .faq-overlap-container {
        grid-template-columns: 1fr 1fr;
        align-items: center;
    }
}
.faq-image-overlap-section .image-container img {
    width: 100%;
    height: auto;
    border-radius: 1rem;
    box-shadow: 0 10px 30px rgba(0,0,0,0.1);
    display: block;
}
.faq-image-overlap-section .accordion-container {
    display: flex;
    flex-direction: column;
    gap: 1rem;
}
@media (min-width: 768px) {
    .faq-image-overlap-section .accordion-container {
        position: relative;
        z-index: 10;
        margin-top: 4rem;
        margin-left: -4rem;
    }
}
.faq-image-overlap-section .faq-item {
    background-color: #f2f2f2;
    border-radius: 0.5rem;
    transition: background-color 0.3s ease;
}
.faq-image-overlap-section .faq-item[open] {
    background-color: #d96666;
    color: #ffffff;
}
.faq-image-overlap-section .faq-item summary {
    padding: 1.25rem 1.5rem;
    font-weight: 600;
    color: var(--color-dark, #1F2937);
    cursor: pointer;
    list-style: none;
    display: flex;
    justify-content: space-between;
    align-items: center;
    transition: color 0.3s ease;
}
.faq-image-overlap-section .faq-item summary::-webkit-details-marker {
    display: none;
}
.faq-image-overlap-section .faq-item[open] summary {
    color: #ffffff;
}
.faq-image-overlap-section .faq-icon {
    font-size: 1.5rem;
    color: #888;
    transition: transform 0.3s ease, color 0.3s ease;
    flex-shrink: 0;
}
.faq-image-overlap-section .faq-item[open] .faq-icon {
    transform: rotate(45deg);
    color: #ffffff;
}
.faq-image-overlap-section .faq-answer {
    padding: 0 1.5rem 1.5rem;
    font-size: 0.95rem;
    line-height: 1.6;
    color: #ffffff;
}
.faq-image-overlap-section .anim-delay-1 { transition-delay: 0.2s; }
.faq-image-overlap-section .anim-delay-2 { transition-delay: 0.4s; }
.faq-image-overlap-section .anim-delay-3 { transition-delay: 0.6s; }
