css:
.section-faq-image-overlay-5 {
  position: relative;
  padding: 80px 0;
  color: var(--color-light);
  overflow: hidden;
}
.section-faq-image-overlay-5 .faq-background-image {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-image: url('https://pixabay.com/api/?key=52494880-f6ccd6f4c90db08ed73d4f14d&q=tech+background&image_type=photo');
  background-size: cover;
  background-position: center;
  z-index: 0;
}
.section-faq-image-overlay-5::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-color: rgba(0, 0, 0, 0.6);
  z-index: 1;
}
.section-faq-image-overlay-5 .container {
  position: relative;
  z-index: 2;
  max-width: 800px;
  margin: 0 auto;
  padding: 0 20px;
}
.section-faq-image-overlay-5 .faq-content-wrapper {
  background: rgba(30, 30, 30, 0.8); /* Slightly transparent dark background for content */
  padding: 40px;
  border-radius: 10px;
}
.section-faq-image-overlay-5 .faq-title {
  text-align: center;
  font-size: clamp(2rem, 5vw, 3rem);
  font-family: var(--font-heading);
  margin-bottom: 40px;
  color: var(--color-light);
}
.section-faq-image-overlay-5 .faq-accordion {
  border: 1px solid rgba(255, 255, 255, 0.3);
  border-radius: 8px;
  overflow: hidden;
}
.section-faq-image-overlay-5 .faq-item {
  border-bottom: 1px solid rgba(255, 255, 255, 0.2);
}
.section-faq-image-overlay-5 .faq-item:last-child {
  border-bottom: none;
}
.section-faq-image-overlay-5 .faq-question {
  width: 100%;
  padding: 20px;
  text-align: left;
  font-family: var(--font-body);
  font-size: 1.1rem;
  font-weight: 600;
  background-color: transparent;
  color: var(--color-light);
  border: none;
  cursor: pointer;
  display: flex;
  justify-content: space-between;
  align-items: center;
  transition: background-color 0.3s ease;
}
.section-faq-image-overlay-5 .faq-question:hover {
  background-color: rgba(255, 255, 255, 0.1);
}
.section-faq-image-overlay-5 .faq-icon {
  font-size: 1.5rem;
  transition: transform 0.3s ease;
}
.section-faq-image-overlay-5 .faq-answer {
  max-height: 0;
  overflow: hidden;
  transition: max-height 0.3s ease-out, padding 0.3s ease-out;
  padding: 0 20px;
  background-color: rgba(30, 30, 30, 0.6);
}
.section-faq-image-overlay-5 .faq-answer p {
  margin: 0;
  padding: 20px 0;
  font-family: var(--font-body);
  font-size: 1rem;
  line-height: 1.6;
  color: var(--color-secondary);
}
.section-faq-image-overlay-5 .faq-item.active .faq-question {
  background-color: rgba(255, 255, 255, 0.1);
}
.section-faq-image-overlay-5 .faq-item.active .faq-question .faq-icon {
  transform: rotate(45deg);
}
.section-faq-image-overlay-5 .faq-item.active .faq-answer {
  max-height: 200px; /* Adjust as needed */
  padding-bottom: 20px;
}
