css:
.faq-ld-section {
    background-color: #fefefc;
    padding: 4rem 1rem;
    font-family: 'Inter', sans-serif;
    color: #000;
}
.faq-ld-section .container {
    max-width: 800px;
    margin: 0 auto;
}
.faq-ld-section .faq-main-title {
    font-size: 1rem;
    font-weight: 700;
    margin-bottom: 2.5rem;
    text-transform: uppercase;
    letter-spacing: 0.1em;
}
.faq-ld-section .faq-category {
    border-bottom: 1px solid #e0e0e0;
    margin-bottom: 0.5rem;
}
.faq-ld-section .faq-category h3 {
    color: #cdaa7d;
    font-size: 0.8rem;
    font-weight: 700;
    letter-spacing: 0.1em;
    padding-bottom: 0.75rem;
    text-transform: uppercase;
}
.faq-ld-section .faq-accordion .faq-item {
    border-bottom: 1px solid #e0e0e0;
}
.faq-ld-section .faq-item summary {
    display: flex;
    justify-content: space-between;
    align-items: center;
    gap: 1rem;
    padding: 1.25rem 0;
    font-weight: 700;
    cursor: pointer;
    list-style: none;
    font-size: 0.9rem;
    text-transform: uppercase;
    letter-spacing: 0.05em;
}
.faq-ld-section .faq-item summary > span:first-child {
    flex: 1;
    min-width: 0; /* Allows text to wrap */
}
.faq-ld-section .faq-item summary::-webkit-details-marker { display: none; }
.faq-ld-section .faq-icon {
    font-size: 1.5rem;
    font-weight: 400;
    flex-shrink: 0;
}
.faq-ld-section .faq-content {
    padding: 0 1rem 1.25rem 0;
    color: #333;
    line-height: 1.6;
    font-size: 1rem;
    text-transform: none;
    letter-spacing: normal;
    font-weight: 400;
}
