css:
.section-faq-minimal-border-x4p .container {
    max-width: 700px;
    margin: 0 auto;
    padding: 50px 20px;
}

.section-faq-minimal-border-x4p .accordion-container {
    background-color: var(--color-surface-1);
    border-radius: 12px;
    overflow: hidden;
}

.section-faq-minimal-border-x4p .faq-item {
    border-bottom: 1px solid var(--color-border);
}

.section-faq-minimal-border-x4p .faq-item:last-child {
    border-bottom: none;
}

.section-faq-minimal-border-x4p .faq-item summary {
    display: flex;
    justify-content: space-between;
    align-items: center;
    cursor: pointer;
    padding: 20px;
    font-family: var(--font-body);
    color: var(--color-dark);
    font-size: 1.1rem;
    font-weight: 500;
    transition: color 0.3s ease;
}

.section-faq-minimal-border-x4p .faq-item[open] summary {
    background-color: var(--color-primary-light);
    color: var(--color-primary);
}

.section-faq-minimal-border-x4p .faq-item summary .faq-question {
    flex-grow: 1;
    margin-right: 15px;
}

.section-faq-minimal-border-x4p .faq-item summary .faq-icon {
    transition: transform 0.3s ease;
    font-size: 0.8em;
    color: var(--color-secondary);
}

.section-faq-minimal-border-x4p .faq-item[open] summary .faq-icon {
    transform: rotate(180deg);
    color: var(--color-primary);
}

.section-faq-minimal-border-x4p .faq-answer {
    padding: 20px;
    background-color: var(--color-light);
    color: var(--color-dark);
    font-family: var(--font-body);
    font-size: 1rem;
    line-height: 1.6;
    border-top: 1px solid var(--color-border);
}

.section-faq-minimal-border-x4p .faq-answer p {
    margin: 0;
}

/* Ensure native markers are hidden */
.section-faq-minimal-border-x4p summary::-webkit-details-marker {
    display: none;
}
.section-faq-minimal-border-x4p summary {
    list-style: none;
}
