css:
.faq-modern-coach-section {
    padding: 5rem 0;
    background-color: var(--color-light, #FFFFFF);
    color: var(--color-dark, #1F2937);
    font-family: sans-serif;
}
.faq-modern-coach-section .container {
    width: 90%;
    max-width: 800px;
    margin: 0 auto;
}
.faq-modern-coach-section .section-title {
    text-align: center;
    font-size: 2.5rem;
    font-weight: 700;
    margin-bottom: 2rem;
}
.faq-modern-coach-section .coach-intro {
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 1rem;
    margin-bottom: 3rem;
}
.faq-modern-coach-section .coach-intro img {
    width: 150px;
    height: 150px;
    border-radius: 50%;
    object-fit: cover;
}
.faq-modern-coach-section .sample-text {
    font-size: 0.9rem;
    color: var(--color-secondary, #6B7280);
    max-width: 300px;
    text-align: center;
}
.faq-modern-coach-section .faq-accordion {
    display: flex;
    flex-direction: column;
    gap: 1rem;
}
.faq-modern-coach-section .faq-item {
    border-bottom: 1px solid #e5e7eb;
}
.faq-modern-coach-section .faq-item summary {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 1rem 0;
    cursor: pointer;
    font-weight: 600;
    list-style: none; /* Remove default marker */
}
.faq-modern-coach-section .faq-item summary::-webkit-details-marker {
    display: none;
}
.faq-modern-coach-section .faq-icon {
    transition: transform 0.3s ease;
    font-size: 1.5rem;
    font-weight: 300;
    color: #2EC5B6;
}
.faq-modern-coach-section .faq-item[open] > summary {
    color: #2EC5B6;
}
.faq-modern-coach-section .faq-item[open] > summary .faq-icon {
    transform: rotate(45deg);
}
.faq-modern-coach-section .faq-answer {
    padding: 1.5rem;
    border: 1px solid #2EC5B6;
    border-radius: 8px;
    margin: 0.5rem 0 1.5rem 0;
    box-shadow: 0 4px 15px rgba(46, 197, 182, 0.1);
}
.faq-modern-coach-section .faq-answer p {
    color: var(--color-secondary, #6B7280);
    line-height: 1.6;
    margin: 0;
}
.faq-modern-coach-section .anim-delay-1 { transition-delay: 0.1s; }
.faq-modern-coach-section .anim-delay-2 { transition-delay: 0.2s; }
