css:
.faq-numbered-accordion-section {
    padding: 5rem 0;
    background-color: #f3f4f6; /* gray-100 */
    font-family: sans-serif;
}
.faq-numbered-accordion-section .container {
    max-width: 900px;
    margin: 0 auto;
    padding: 0 1rem;
}
.faq-numbered-accordion-section .section-header {
    text-align: center;
    margin-bottom: 3rem;
}
.faq-numbered-accordion-section h2 {
    font-size: clamp(2rem, 5vw, 3rem);
    font-weight: 700;
    color: var(--color-dark, #1F2937);
    margin-bottom: 1rem;
}
.faq-numbered-accordion-section .section-header p {
    color: var(--color-secondary, #6B7280);
    max-width: 600px;
    margin: 0 auto;
}
.faq-numbered-accordion-section .faq-list-wrapper {
    background-color: var(--color-light, #FFFFFF);
    border-radius: 1.5rem; /* 24px */
    padding: 2rem;
    box-shadow: 0 10px 25px rgba(0,0,0,0.05);
}
.faq-numbered-accordion-section .faq-accordion {
    display: flex;
    flex-direction: column;
    gap: 1rem;
}
.faq-numbered-accordion-section .faq-item summary {
    display: flex;
    align-items: center;
    gap: 1rem;
    padding: 1rem;
    cursor: pointer;
    list-style: none;
    font-weight: 600;
    color: var(--color-dark, #1F2937);
}
.faq-numbered-accordion-section .faq-item summary::-webkit-details-marker { display: none; }
.faq-numbered-accordion-section .faq-number {
    background-color: #f3f4f6;
    border-radius: 8px;
    width: 40px;
    height: 40px;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 0.9rem;
    flex-shrink: 0;
}
.faq-numbered-accordion-section .faq-question {
    flex-grow: 1;
}
.faq-numbered-accordion-section .faq-icon {
    width: 28px;
    height: 28px;
    border-radius: 8px;
    background-color: #e5e7eb;
    color: #6b7280;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 1.5rem;
    line-height: 1;
    transition: transform 0.3s;
    flex-shrink: 0;
}
.faq-numbered-accordion-section .faq-item[open] > summary .faq-icon {
    transform: rotate(45deg);
}
.faq-numbered-accordion-section .faq-item p {
    padding: 0 1rem 1rem calc(40px + 2rem); /* Align with question text */
    color: var(--color-secondary, #6B7280);
    margin: 0;
    line-height: 1.6;
}
.faq-numbered-accordion-section .anim-delay-1 { transition-delay: 0.1s; }
