css:
.section-faq-outlined-buttons-b2f .container {
    max-width: 800px;
    margin: 0 auto;
    padding: 50px 20px;
}

.section-faq-outlined-buttons-b2f .section-header {
    text-align: center;
    margin-bottom: 50px;
}

.section-faq-outlined-buttons-b2f .section-title {
    font-family: var(--font-heading);
    font-size: clamp(2rem, 5vw, 3rem);
    color: var(--color-dark);
    margin-bottom: 15px;
}

.section-faq-outlined-buttons-b2f .section-subtitle {
    font-family: var(--font-body);
    font-size: 1.1rem;
    color: var(--color-secondary);
    line-height: 1.6;
}

.section-faq-outlined-buttons-b2f .button-group {
    display: flex;
    justify-content: center;
    flex-wrap: wrap;
    gap: 15px;
    margin-bottom: 40px;
}

.section-faq-outlined-buttons-b2f .btn-outline {
    background: none;
    border: 2px solid var(--color-primary);
    color: var(--color-primary);
    padding: 10px 25px;
    border-radius: 25px;
    font-family: var(--font-body);
    font-size: 1rem;
    font-weight: 600;
    cursor: pointer;
    transition: all 0.3s ease;
}

.section-faq-outlined-buttons-b2f .btn-outline:hover {
    background-color: var(--color-primary);
    color: var(--color-light);
}

.section-faq-outlined-buttons-b2f .btn-outline.active {
    background-color: var(--color-primary);
    color: var(--color-light);
}

.section-faq-outlined-buttons-b2f .accordion-container {
    background-color: var(--color-surface-1);
    border-radius: 12px;
    box-shadow: 0 5px 20px rgba(0, 0, 0, 0.05);
    overflow: hidden;
}

.section-faq-outlined-buttons-b2f .faq-item {
    border-bottom: 1px solid var(--color-border);
}

.section-faq-outlined-buttons-b2f .faq-item:last-child {
    border-bottom: none;
}

.section-faq-outlined-buttons-b2f .faq-item summary {
    display: flex;
    justify-content: space-between;
    align-items: center;
    cursor: pointer;
    padding: 20px;
    font-family: var(--font-body);
    color: var(--color-dark);
    font-size: 1.1rem;
    font-weight: 500;
    transition: color 0.3s ease;
}

.section-faq-outlined-buttons-b2f .faq-item[open] summary {
    background-color: var(--color-primary-light);
    color: var(--color-primary);
}

.section-faq-outlined-buttons-b2f .faq-item summary .faq-question {
    flex-grow: 1;
    margin-right: 15px;
}

.section-faq-outlined-buttons-b2f .faq-item summary .faq-icon {
    transition: transform 0.3s ease;
    font-size: 0.8em;
    color: var(--color-secondary);
}

.section-faq-outlined-buttons-b2f .faq-item[open] summary .faq-icon {
    transform: rotate(180deg);
    color: var(--color-primary);
}

.section-faq-outlined-buttons-b2f .faq-answer {
    padding: 20px;
    background-color: var(--color-light);
    color: var(--color-dark);
    font-family: var(--font-body);
    font-size: 1rem;
    line-height: 1.6;
    border-top: 1px solid var(--color-border);
}

.section-faq-outlined-buttons-b2f .faq-answer p {
    margin: 0;
}

/* Ensure native markers are hidden */
.section-faq-outlined-buttons-b2f summary::-webkit-details-marker {
    display: none;
}
.section-faq-outlined-buttons-b2f summary {
    list-style: none;
}
