css:
.faq-rounded-section {
    padding: 5rem 0;
    background-color: var(--color-light, #FFFFFF);
    color: var(--color-dark, #1F2937);
    font-family: sans-serif;
}
.faq-rounded-section .container {
    width: 90%;
    max-width: 800px;
    margin: 0 auto;
}
.faq-rounded-section .section-header {
    text-align: center;
    margin-bottom: 3rem;
}
.faq-rounded-section h2 {
    font-size: clamp(2.5rem, 5vw, 3rem);
    font-weight: 700;
    margin-bottom: 1rem;
}
.faq-rounded-section .section-header p {
    color: var(--color-secondary, #6B7280);
    max-width: 500px;
    margin: 0 auto;
}
.faq-rounded-section .faq-accordion {
    display: flex;
    flex-direction: column;
    gap: 1rem;
}
.faq-rounded-section .faq-item {
    background-color: #f3f4f6; /* gray-100 */
    border-radius: 12px;
    box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.05), 0 2px 4px -2px rgba(0, 0, 0, 0.05);
}
.faq-rounded-section .faq-item[open] {
    background-color: #f9fafb; /* gray-50 */
}
.faq-rounded-section .faq-item summary {
    padding: 1.25rem 1.5rem;
    cursor: pointer;
    font-weight: 600;
    font-size: 1.1rem;
    list-style: none; /* Remove default marker */
    display: flex;
    justify-content: space-between;
    align-items: center;
}
.faq-rounded-section .faq-item summary::-webkit-details-marker { display: none; }
.faq-rounded-section .icon-wrapper {
    background-color: #e5e7eb; /* gray-200 */
    border-radius: 6px;
    width: 28px;
    height: 28px;
    display: flex;
    align-items: center;
    justify-content: center;
    color: #6b7280; /* gray-500 */
    transition: transform 0.3s ease;
    flex-shrink: 0;
}
.faq-rounded-section .faq-item[open] summary .icon-wrapper {
    transform: rotate(180deg);
}
.faq-rounded-section .faq-content {
    padding: 0 1.5rem 1.25rem;
    border-top: 1px solid #e5e7eb;
    margin: 0 1.5rem;
}
.faq-rounded-section .faq-content p {
    color: var(--color-secondary, #6B7280);
    margin: 1rem 0 0 0;
    line-height: 1.7;
}
.faq-rounded-section .anim-delay-1 { transition-delay: 0.1s; }
