css:
.faq-ruhiges-section {
    padding: 5rem 0;
    background-color: var(--color-light, #FFFFFF);
    color: var(--color-dark, #1F2937);
    font-family: 'Inter', sans-serif;
}
.faq-ruhiges-section .container {
    width: 90%;
    max-width: 800px;
    margin: 0 auto;
}
.faq-ruhiges-section .section-header {
    text-align: center;
    margin-bottom: 3rem;
}
.faq-ruhiges-section h2 {
    font-family: 'Poppins', sans-serif;
    font-size: clamp(2.5rem, 5vw, 3rem);
    font-weight: 800;
}
.faq-ruhiges-section .faq-accordion {
    display: flex;
    flex-direction: column;
    gap: 1rem;
}
.faq-ruhiges-section .faq-item {
    background-color: #f9fafb; /* gray-50 */
    border: 1px solid #e5e7eb; /* gray-200 */
    border-radius: 12px;
    transition: box-shadow 0.3s ease;
}
.faq-ruhiges-section .faq-item[open] {
    border-color: var(--color-primary, #667eea);
}
.faq-ruhiges-section .faq-item summary {
    display: flex;
    justify-content: space-between;
    align-items: center;
    gap: 1rem;
    padding: 1.5rem;
    cursor: pointer;
    list-style: none;
    font-weight: 600;
    font-size: 1.1rem;
}
.faq-ruhiges-section .faq-item summary::-webkit-details-marker {
    display: none;
}
.faq-ruhiges-section .faq-icon {
    width: 28px;
    height: 28px;
    border-radius: 50%;
    background-color: #e5e7eb; /* gray-200 */
    color: var(--color-secondary, #6B7280);
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 1.5rem;
    line-height: 1;
    transition: transform 0.3s ease, background-color 0.3s ease, color 0.3s ease;
    flex-shrink: 0;
}
.faq-ruhiges-section .faq-item[open] > summary .faq-icon {
    transform: rotate(45deg);
    background-color: var(--color-primary, #667eea);
    color: var(--color-light, #FFFFFF);
}
.faq-ruhiges-section .faq-content {
    padding: 0 1.5rem 1.5rem;
}
.faq-ruhiges-section .faq-content p {
    color: var(--color-secondary, #6B7280);
    margin: 0;
    line-height: 1.7;
    max-width: 90%;
}
.faq-ruhiges-section .anim-delay-1 { transition-delay: 0.1s; }
