css:
.faq-simple-section {
    padding: 5rem 0;
    background-color: var(--color-light, #FFFFFF);
    color: var(--color-dark, #1F2937);
}
.faq-simple-section .container {
    width: 90%;
    max-width: 800px;
    margin: 0 auto;
}
.faq-simple-section .section-header {
    text-align: center;
    margin-bottom: 3rem;
}
.faq-simple-section h2 {
    font-size: clamp(2.5rem, 5vw, 3rem);
    font-weight: 700;
    margin-bottom: 1rem;
}
.faq-simple-section .section-header p {
    color: var(--color-secondary, #6B7280);
    max-width: 500px;
    margin: 0 auto;
}
.faq-simple-section .faq-accordion {
    display: flex;
    flex-direction: column;
    gap: 1rem;
}
.faq-simple-section .faq-item {
    background-color: #f3f4f6; /* gray-100 */
    border-radius: 0.75rem;
}
.faq-simple-section .faq-item[open] {
    background-color: #e5e7eb; /* gray-200 */
}
.faq-simple-section .faq-item summary {
    padding: 1.5rem;
    cursor: pointer;
    font-weight: 600;
    font-size: 1.1rem;
    position: relative;
    list-style: none; /* Remove default marker */
}
.faq-simple-section .faq-item summary::-webkit-details-marker { display: none; }
.faq-simple-section .faq-item summary::after {
    content: '+';
    position: absolute;
    right: 1.5rem;
    top: 50%;
    transform: translateY(-50%) rotate(0deg);
    font-size: 1.5rem;
    transition: transform 0.3s;
    color: var(--color-secondary, #6B7280);
    background-color: #e5e7eb;
    width: 24px;
    height: 24px;
    display: flex;
    align-items: center;
    justify-content: center;
    border-radius: 50%;
}
.faq-simple-section .faq-item[open] summary::after {
    transform: translateY(-50%) rotate(45deg);
}
.faq-simple-section .faq-item p {
    padding: 0 1.5rem 1.5rem;
    color: var(--color-secondary, #6B7280);
    margin: 0;
    line-height: 1.7;
}
.faq-simple-section .anim-delay-1 { transition-delay: 0.1s; }
