css:
.faq-smalhelp-section {
    padding: 4rem 0;
    background-color: var(--color-light, #FFFFFF);
    font-family: sans-serif;
}
.faq-smalhelp-section .container {
    width: 90%;
    max-width: 1000px;
    margin: 0 auto;
    padding: 0 1rem;
}
.faq-smalhelp-container {
    display: grid;
    grid-template-columns: 1fr;
    gap: 2rem;
    border: 2px dotted #e5e7eb; /* gray-200 */
    border-radius: 1rem;
    padding: 2rem;
}
@media (min-width: 768px) {
    .faq-smalhelp-container {
        grid-template-columns: 1fr 2fr;
        gap: 3rem;
    }
}

.faq-smalhelp-section .help-card {
    background-color: #FEF6F6; /* light pink */
    border-radius: 1rem;
    padding: 2rem;
    display: flex;
    flex-direction: column;
    gap: 1.5rem;
}
.faq-smalhelp-section .help-card h2 {
    font-size: 1.75rem;
    font-weight: 700;
    color: var(--color-dark, #1F2937);
}
.faq-smalhelp-section .contact-item {
    display: flex;
    align-items: center;
    gap: 1rem;
}
.faq-smalhelp-section .icon-wrapper {
    width: 50px;
    height: 50px;
    border-radius: 50%;
    background-color: #1ABC9C;
    color: white;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 1.25rem;
    flex-shrink: 0;
}
.faq-smalhelp-section .contact-text .main-text {
    font-weight: 600;
    color: var(--color-dark, #1F2937);
    margin: 0;
}
.faq-smalhelp-section .contact-text .sub-text {
    font-size: 0.9rem;
    color: var(--color-secondary, #6B7280);
    margin: 0;
}

.faq-smalhelp-section .faq-accordion {
    display: flex;
    flex-direction: column;
}
.faq-smalhelp-section .faq-item {
    border-bottom: 1px solid #e5e7eb; /* gray-200 */
    padding-bottom: 1rem;
    margin-bottom: 1rem;
}
.faq-smalhelp-section .faq-item:last-child {
    border-bottom: none;
    margin-bottom: 0;
}
.faq-smalhelp-section .faq-item summary {
    display: flex;
    justify-content: space-between;
    align-items: center;
    cursor: pointer;
    list-style: none;
    font-weight: 600;
    color: var(--color-dark, #1F2937);
    padding-right: 1rem;
}
.faq-smalhelp-section .faq-item summary::-webkit-details-marker {
    display: none;
}
.faq-smalhelp-section .faq-item summary::after {
    content: '▼';
    font-size: 0.8rem;
    transition: transform 0.3s;
}
.faq-smalhelp-section .faq-item[open] summary::after {
    transform: rotate(180deg);
}
.faq-smalhelp-section .faq-content {
    padding-top: 1rem;
}
.faq-smalhelp-section .faq-content p {
    color: var(--color-secondary, #6B7280);
    line-height: 1.6;
    margin: 0;
}
