css:
.faq-cta-card-section {
    padding: 5rem 0;
    background-color: var(--color-light, #FFFFFF);
}
.faq-cta-card-section .container {
    width: 90%;
    max-width: 1200px;
    margin: 0 auto;
}
.faq-cta-card-container {
    display: grid;
    grid-template-columns: 1fr;
    gap: 3rem;
    align-items: flex-start;
}
@media (min-width: 992px) {
    .faq-cta-card-container {
        grid-template-columns: 1fr 1.5fr;
    }
}
.faq-cta-card-section .cta-card {
    position: relative;
    border-radius: 1.5rem;
    overflow: hidden;
    color: var(--color-light, #FFFFFF);
    padding: 2rem;
    display: flex;
    flex-direction: column;
    justify-content: flex-end;
    min-height: 400px;
}
.faq-cta-card-section .cta-card img {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    object-fit: cover;
    z-index: 0;
}
.faq-cta-card-section .cta-card-overlay {
    position: absolute;
    inset: 0;
    background: linear-gradient(to top, rgba(0,0,0,0.8) 0%, rgba(0,0,0,0.2) 60%, transparent 100%);
    z-index: 1;
}
.faq-cta-card-section .cta-card-content {
    position: relative;
    z-index: 2;
}
.faq-cta-card-section .cta-card h3 {
    font-size: 2rem;
    font-weight: 700;
    margin-bottom: 1rem;
}
.faq-cta-card-section .cta-card p {
    margin-bottom: 1.5rem;
    opacity: 0.9;
}
.faq-cta-card-section .btn-light {
    background-color: var(--color-light, #FFFFFF);
    color: var(--color-dark, #1F2937);
    padding: 0.75rem 1.5rem;
    border-radius: 8px;
    text-decoration: none;
    font-weight: 600;
    display: inline-block;
}

.faq-cta-card-section .faq-accordion {
    display: flex;
    flex-direction: column;
    gap: 1rem;
}
.faq-cta-card-section .faq-item {
    background-color: #f3f4f6; /* gray-100 */
    border-radius: 12px;
}
.faq-cta-card-section .faq-item summary {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 1.25rem 1.5rem;
    cursor: pointer;
    list-style: none;
    font-weight: 600;
}
.faq-cta-card-section .faq-item summary::-webkit-details-marker { display: none; }
.faq-cta-card-section .faq-icon {
    width: 28px;
    height: 28px;
    border-radius: 8px;
    background-color: #e5e7eb;
    color: #6b7280;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 1.5rem;
    line-height: 1;
    transition: transform 0.3s;
    flex-shrink: 0;
}
.faq-cta-card-section .faq-item[open] > summary .faq-icon {
    transform: rotate(45deg);
}
.faq-cta-card-section .faq-item p {
    padding: 0 1.5rem 1.25rem;
    color: var(--color-secondary, #6B7280);
    margin: 0;
}
