css:
.faq-split-section {
    padding: 5rem 0;
    background-color: var(--color-light, #FFFFFF);
    color: var(--color-dark, #1F2937);
}
.faq-split-section .container {
    width: 90%;
    max-width: 1100px;
    margin: 0 auto;
}
.faq-split-container {
    display: grid;
    grid-template-columns: 1fr;
    gap: 3rem;
    align-items: flex-start;
}
@media (min-width: 992px) {
    .faq-split-container {
        grid-template-columns: 1fr 1.5fr;
    }
}
.faq-split-section .faq-header h2 {
    font-size: clamp(3rem, 6vw, 4rem);
    font-weight: 900;
    line-height: 1.1;
    margin-bottom: 1.5rem;
}
.faq-split-section .faq-header p {
    color: var(--color-secondary, #6B7280);
    font-size: 1.1rem;
    line-height: 1.7;
}
.faq-split-section .faq-accordion {
    display: flex;
    flex-direction: column;
    gap: 0;
}
.faq-split-section .faq-item {
    border-bottom: 1px solid #e5e7eb; /* gray-200 */
}
.faq-split-section .faq-item summary {
    padding: 1.5rem 0;
    cursor: pointer;
    font-weight: 600;
    font-size: 1.1rem;
    position: relative;
    list-style: none;
    padding-right: 2.5rem;
}
.faq-split-section .faq-item summary::-webkit-details-marker { display: none; }
.faq-split-section .faq-item summary::after {
    content: '+';
    position: absolute;
    right: 0.5rem;
    top: 50%;
    transform: translateY(-50%) rotate(0deg);
    font-size: 1.5rem;
    transition: transform 0.3s;
    color: var(--color-secondary, #6B7280);
    background-color: #e5e7eb;
    width: 24px;
    height: 24px;
    display: flex;
    align-items: center;
    justify-content: center;
    border-radius: 50%;
}
.faq-split-section .faq-item[open] summary::after {
    transform: translateY(-50%) rotate(45deg);
}
.faq-split-section .faq-item p {
    padding: 0 0 1.5rem 0;
    color: var(--color-secondary, #6B7280);
    margin: 0;
    line-height: 1.7;
}
