css:
.faq-split-simple-section {
    padding: 5rem 0;
    background-color: #f3f4f6; /* gray-100 */
    font-family: sans-serif;
}
.faq-split-simple-section .container {
    max-width: 1100px;
    margin: 0 auto;
    padding: 0 1rem;
}
.faq-split-simple-container {
    display: grid;
    grid-template-columns: 1fr;
    gap: 3rem;
    align-items: flex-start;
}
@media (min-width: 992px) {
    .faq-split-simple-container {
        grid-template-columns: 1fr 1.5fr;
    }
}
.faq-split-simple-section .faq-header h2 {
    font-size: clamp(2.5rem, 6vw, 3.5rem);
    font-weight: 700;
    color: var(--color-dark, #1F2937);
    margin-bottom: 1.5rem;
}
.faq-split-simple-section .faq-header p {
    color: var(--color-secondary, #6B7280);
    line-height: 1.7;
}
.faq-split-simple-section .faq-accordion {
    display: flex;
    flex-direction: column;
    gap: 1rem;
}
.faq-split-simple-section .faq-item {
    background-color: var(--color-light, #FFFFFF);
    border-radius: 1rem;
    box-shadow: 0 4px 15px rgba(0,0,0,0.05);
}
.faq-split-simple-section .faq-item summary {
    display: flex;
    justify-content: space-between;
    align-items: center;
    gap: 1rem;
    padding: 1.5rem;
    cursor: pointer;
    list-style: none;
    font-weight: 600;
    color: var(--color-dark, #1F2937);
}
.faq-split-simple-section .faq-item summary::-webkit-details-marker { display: none; }
.faq-split-simple-section .faq-icon {
    width: 28px; height: 28px;
    border-radius: 8px;
    background-color: #e5e7eb;
    color: #6b7280;
    display: flex; align-items: center; justify-content: center;
    font-size: 1.5rem; line-height: 1;
    transition: transform 0.3s;
    flex-shrink: 0;
}
.faq-split-simple-section .faq-item[open] > summary .faq-icon {
    transform: rotate(45deg);
}
.faq-split-simple-section .faq-item p {
    padding: 0 1.5rem 1.5rem;
    color: var(--color-secondary, #6B7280);
    margin: 0;
    line-height: 1.6;
}
