css:
.faq-static-grid-section {
    padding: 5rem 0;
    background-color: var(--color-light, #FFFFFF);
    color: var(--color-dark, #1F2937);
}
.faq-static-grid-section .container {
    width: 90%;
    max-width: 1100px;
    margin: 0 auto;
}
.faq-static-grid-section .section-header {
    text-align: center;
    margin-bottom: 4rem;
}
.faq-static-grid-section h2 {
    font-size: clamp(2.5rem, 5vw, 3rem);
    font-weight: 700;
    margin-bottom: 1rem;
}
.faq-static-grid-section .section-header p {
    color: var(--color-secondary, #6B7280);
    max-width: 500px;
    margin: 0 auto;
}
.faq-static-grid-section .faq-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
    gap: 2rem;
}
.faq-static-grid-section .faq-card {
    background-color: #f3f4f6; /* gray-100 */
    border-radius: 1rem;
    padding: 2rem;
    display: flex;
    flex-direction: column;
    gap: 0.75rem;
}
.faq-static-grid-section .faq-card h3 {
    font-size: 1.25rem;
    font-weight: 600;
    color: var(--color-dark, #1F2937);
}
.faq-static-grid-section .faq-card p {
    color: var(--color-secondary, #6B7280);
    line-height: 1.6;
    margin: 0;
}
.faq-static-grid-section .anim-delay-1 { transition-delay: 0.1s; }
.faq-static-grid-section .anim-delay-2 { transition-delay: 0.2s; }
.faq-static-grid-section .anim-delay-3 { transition-delay: 0.3s; }
.faq-static-grid-section .anim-delay-4 { transition-delay: 0.4s; }
.faq-static-grid-section .anim-delay-5 { transition-delay: 0.5s; }
.faq-static-grid-section .anim-delay-6 { transition-delay: 0.6s; }
