css:
.faq-static-lined-list-section {
    padding: 5rem 0;
    background-color: var(--color-light, #FFFFFF);
    color: var(--color-dark, #1F2937);
}
.faq-static-lined-list-section .container {
    width: 90%;
    max-width: 900px;
    margin: 0 auto;
}
.faq-static-lined-list-section .section-header {
    text-align: center;
    margin-bottom: 4rem;
}
.faq-static-lined-list-section h2 {
    font-size: clamp(2.5rem, 5vw, 3rem);
    font-weight: 700;
}
.faq-static-lined-list-section .faq-list {
    display: flex;
    flex-direction: column;
}
.faq-static-lined-list-section .faq-item {
    display: grid;
    grid-template-columns: 1fr;
    gap: 1rem;
    padding: 2rem 0;
    border-bottom: 1px solid #e5e7eb; /* gray-200 */
}
.faq-static-lined-list-section .faq-item:first-child {
    padding-top: 0;
    border-top: 1px solid #e5e7eb;
}
.faq-static-lined-list-section .faq-item:last-child {
    border-bottom: none;
    padding-bottom: 0;
}
@media (min-width: 768px) {
    .faq-static-lined-list-section .faq-item {
        grid-template-columns: 1fr 1.5fr;
        gap: 2rem;
    }
}
.faq-static-lined-list-section .faq-question h4 {
    font-size: 1.25rem;
    font-weight: 600;
    color: var(--color-dark, #1F2937);
}
.faq-static-lined-list-section .faq-answer p {
    color: var(--color-secondary, #6B7280);
    line-height: 1.6;
    margin: 0;
}
.faq-static-lined-list-section .anim-delay-1 { transition-delay: 0.1s; }
.faq-static-lined-list-section .anim-delay-2 { transition-delay: 0.2s; }
.faq-static-lined-list-section .anim-delay-3 { transition-delay: 0.3s; }
.faq-static-lined-list-section .anim-delay-4 { transition-delay: 0.4s; }
