css:
.faq-static-two-col-section {
    padding: 5rem 0;
    background-color: var(--color-light, #FFFFFF);
    color: var(--color-dark, #1F2937);
}
.faq-static-two-col-section .container {
    width: 90%;
    max-width: 900px;
    margin: 0 auto;
}
.faq-static-two-col-section .section-header {
    text-align: center;
    margin-bottom: 4rem;
}
.faq-static-two-col-section h2 {
    font-size: clamp(2.5rem, 5vw, 3rem);
    font-weight: 700;
    margin-bottom: 1rem;
}
.faq-static-two-col-section .section-header p {
    color: var(--color-secondary, #6B7280);
    max-width: 500px;
    margin: 0 auto;
}
.faq-static-two-col-section .faq-list {
    display: flex;
    flex-direction: column;
    gap: 2.5rem;
}
.faq-static-two-col-section .faq-item {
    display: grid;
    grid-template-columns: 1fr;
    gap: 1rem;
}
@media (min-width: 768px) {
    .faq-static-two-col-section .faq-item {
        grid-template-columns: 1fr 1fr;
        gap: 2rem;
    }
}
.faq-static-two-col-section .faq-question h4 {
    font-size: 1.25rem;
    font-weight: 600;
    color: var(--color-dark, #1F2937);
}
.faq-static-two-col-section .faq-answer p {
    color: var(--color-secondary, #6B7280);
    line-height: 1.6;
    margin: 0;
}
.faq-static-two-col-section .anim-delay-1 { transition-delay: 0.1s; }
.faq-static-two-col-section .anim-delay-2 { transition-delay: 0.2s; }
.faq-static-two-col-section .anim-delay-3 { transition-delay: 0.3s; }
.faq-static-two-col-section .anim-delay-4 { transition-delay: 0.4s; }
