css:
.section-faq-timeline-connector-v1 {
  padding: 5rem 0;
  background-color: var(--color-light);
}
.section-faq-timeline-connector-v1 .container {
  max-width: 800px;
  margin: 0 auto;
  padding: 0 1.5rem;
}
.section-faq-timeline-connector-v1 h2 {
  text-align: center;
  margin-bottom: 3rem;
  font-family: var(--font-heading);
  color: var(--color-dark);
}
.section-faq-timeline-connector-v1 .faq-item {
  position: relative;
}
.section-faq-timeline-connector-v1 summary {
  list-style: none;
  display: flex;
  cursor: pointer;
}
.section-faq-timeline-connector-v1 summary::-webkit-details-marker {
  display: none;
}
.section-faq-timeline-connector-v1 .marker-col {
  display: flex;
  flex-direction: column;
  align-items: center;
  width: 40px;
  flex-shrink: 0;
  margin-right: 1rem;
}
.section-faq-timeline-connector-v1 .dot {
  width: 12px;
  height: 12px;
  background: var(--color-primary);
  border-radius: 50%;
  margin-top: 8px;
  z-index: 2;
}
.section-faq-timeline-connector-v1 .line {
  width: 2px;
  background: var(--color-border);
  flex-grow: 1;
  min-height: 20px;
}
.section-faq-timeline-connector-v1 .faq-item:last-child .line {
  display: none;
}
.section-faq-timeline-connector-v1 .content-col {
  flex-grow: 1;
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 0.5rem 0;
  border-bottom: 1px solid var(--color-border);
}
.section-faq-timeline-connector-v1 .faq-question {
  font-weight: 600;
  font-family: var(--font-body);
  color: var(--color-dark);
}
.section-faq-timeline-connector-v1 .faq-toggle {
  font-size: 1.5rem;
  line-height: 1;
  color: var(--color-secondary);
}
.section-faq-timeline-connector-v1 details[open] .faq-toggle {
  transform: rotate(45deg);
  display: inline-block;
}
.section-faq-timeline-connector-v1 .faq-answer {
  display: flex;
}
.section-faq-timeline-connector-v1 .answer-pad {
  margin-left: 56px;
  padding-bottom: 2rem;
  color: var(--color-secondary);
}
