css:
.faq-two-col-accordion-section {
padding: 4rem 1rem;
font-family: sans-serif;
background-color: var(--color-light, #FFFFFF);
}
.faq-two-col-accordion-section .container {
max-width: 1200px;
width: 100%;
margin: 0 auto;
padding: 2rem 1rem;
border: 1px dashed #ccc;
display: grid;
grid-template-columns: 1fr;
gap: 1.5rem;
box-sizing: border-box;
overflow-x: hidden;
}
@media (min-width: 992px) {
.faq-two-col-accordion-section .container {
grid-template-columns: 1fr 1fr;
gap: 3rem;
padding: 3rem;
}
}
.faq-two-col-accordion-section .left-col h2 {
font-size: clamp(1.75rem, 4.5vw, 2.25rem);
font-weight: 700;
line-height: 1.2;
margin-bottom: 1.5rem;
color: var(--color-dark, #1F2937);
word-wrap: break-word;
overflow-wrap: anywhere;
}
.faq-two-col-accordion-section .image-wrapper {
margin-bottom: 1.5rem;
border-radius: 8px;
overflow: hidden;
}
.faq-two-col-accordion-section .image-wrapper img {
width: 100%;
max-width: 100%;
height: auto;
object-fit: cover;
display: block;
}
.faq-two-col-accordion-section .left-col p {
color: var(--color-secondary, #6B7280);
line-height: 1.65;
margin-bottom: 1.5rem;
}
.faq-two-col-accordion-section .btn-primary {
display: inline-block;
background-color: #dd7469;
color: var(--color-light, #FFFFFF);
padding: 0.8rem 2rem;
text-decoration: none;
border-radius: 4px;
font-weight: 500;
transition: background-color 0.3s;
white-space: nowrap;
}
.faq-two-col-accordion-section .btn-primary:hover {
background-color: #c96358;
}
.faq-two-col-accordion-section .accordion {
display: flex;
flex-direction: column;
gap: 1rem;
width: 100%;
}
.faq-two-col-accordion-section .accordion-item {
border-bottom: 1px solid #e0e0e0;
}
.faq-two-col-accordion-section .accordion-item summary {
display: flex;
justify-content: space-between;
align-items: center;
padding: 1rem 0;
font-weight: 600;
color: var(--color-dark, #1F2937);
cursor: pointer;
list-style: none;
transition: color 0.3s;
outline: none;
gap: 0.75rem;
}
.faq-two-col-accordion-section .accordion-item summary::-webkit-details-marker {
display: none;
}
.faq-two-col-accordion-section .accordion-item summary .icon {
width: 28px;
height: 28px;
border-radius: 50%;
background-color: #dd7469;
color: var(--color-light, #FFFFFF);
display: flex;
align-items: center;
justify-content: center;
transition: transform 0.3s;
font-size: 1.25rem;
line-height: 1;
flex-shrink: 0;
}
.faq-two-col-accordion-section .accordion-item[open] > summary {
color: #dd7469;
}
.faq-two-col-accordion-section .accordion-item[open] > summary .icon {
transform: rotate(45deg);
}
.faq-two-col-accordion-section .accordion-content {
padding-bottom: 1.25rem;
}
.faq-two-col-accordion-section .accordion-content p {
color: var(--color-secondary, #6B7280);
margin-bottom: 0.75rem;
}
.faq-two-col-accordion-section .accordion-content ul {
color: var(--color-secondary, #6B7280);
padding-left: 1.25rem;
display: flex;
flex-direction: column;
gap: 0.5rem;
margin: 0;
}
.faq-two-col-accordion-section .accordion-content li {
padding-left: 0.5rem;
}
.faq-two-col-accordion-section .anim-delay-1 { transition-delay: 0.1s; }
.faq-two-col-accordion-section .anim-delay-2 { transition-delay: 0.2s; }
.faq-two-col-accordion-section .anim-delay-3 { transition-delay: 0.3s; }
*, *::before, *::after { box-sizing: border-box; }
html, body { width: 100%; max-width: 100%; overflow-x: hidden; }
.faq-two-col-accordion-section .container,
.faq-two-col-accordion-section .accordion {
overflow-x: hidden;
}
.faq-two-col-accordion-section img,
.faq-two-col-accordion-section svg,
.faq-two-col-accordion-section video,
.faq-two-col-accordion-section canvas {
max-width: 100%;
height: auto;
display: block;
}
.faq-two-col-accordion-section .left-col a { word-break: break-word; }
