css:
.section-faq-with-callout-box-l9k .container {
    max-width: 900px;
    margin: 0 auto;
    padding: 50px 20px;
}

.section-faq-with-callout-box-l9k .faq-content {
    display: flex;
    flex-wrap: wrap;
    gap: 40px;
    align-items: flex-start;
}

.section-faq-with-callout-box-l9k .callout-box {
    flex: 1;
    min-width: 250px;
    background-color: var(--color-primary-light);
    border-radius: 12px;
    padding: 30px;
    text-align: center;
    box-shadow: 0 5px 20px rgba(0, 0, 0, 0.08);
}

.section-faq-with-callout-box-l9k .callout-title {
    font-family: var(--font-heading);
    font-size: 1.5rem;
    color: var(--color-primary);
    margin-bottom: 15px;
}

.section-faq-with-callout-box-l9k .callout-text {
    font-family: var(--font-body);
    font-size: 1rem;
    color: var(--color-dark);
    line-height: 1.7;
    margin-bottom: 25px;
}

.section-faq-with-callout-box-l9k .btn-primary {
    background-color: var(--color-primary);
    color: var(--color-light);
    padding: 12px 30px;
    border-radius: 25px;
    text-decoration: none;
    font-weight: 600;
    display: inline-block;
    transition: background-color 0.3s ease;
}

.section-faq-with-callout-box-l9k .btn-primary:hover {
    background-color: var(--color-primary-dark);
}

.section-faq-with-callout-box-l9k .accordion-container {
    flex: 1.5;
    min-width: 300px;
    background-color: var(--color-surface-1);
    border-radius: 12px;
    box-shadow: 0 5px 20px rgba(0, 0, 0, 0.05);
    overflow: hidden;
}

.section-faq-with-callout-box-l9k .faq-item {
    border-bottom: 1px solid var(--color-border);
}

.section-faq-with-callout-box-l9k .faq-item:last-child {
    border-bottom: none;
}

.section-faq-with-callout-box-l9k .faq-item summary {
    display: flex;
    justify-content: space-between;
    align-items: center;
    cursor: pointer;
    padding: 20px;
    font-family: var(--font-body);
    color: var(--color-dark);
    font-size: 1.1rem;
    font-weight: 500;
    transition: color 0.3s ease;
}

.section-faq-with-callout-box-l9k .faq-item[open] summary {
    background-color: var(--color-primary);
    color: var(--color-light);
}

.section-faq-with-callout-box-l9k .faq-item summary .faq-question {
    flex-grow: 1;
    margin-right: 15px;
}

.section-faq-with-callout-box-l9k .faq-item summary .faq-icon {
    transition: transform 0.3s ease;
    font-size: 0.8em;
    color: var(--color-secondary);
}

.section-faq-with-callout-box-l9k .faq-item[open] summary .faq-icon {
    transform: rotate(180deg);
    color: var(--color-light);
}

.section-faq-with-callout-box-l9k .faq-answer {
    padding: 20px;
    background-color: var(--color-light);
    color: var(--color-dark);
    font-family: var(--font-body);
    font-size: 1rem;
    line-height: 1.6;
    border-top: 1px solid var(--color-border);
}

.section-faq-with-callout-box-l9k .faq-answer p {
    margin: 0;
}

/* Ensure native markers are hidden */
.section-faq-with-callout-box-l9k summary::-webkit-details-marker {
    display: none;
}
.section-faq-with-callout-box-l9k summary {
    list-style: none;
}
