css:
.section-faq-with-icon-highlight-g4h .container {
    max-width: 800px;
    margin: 0 auto;
    padding: 50px 20px;
}

.section-faq-with-icon-highlight-g4h .accordion-container {
    background-color: var(--color-surface-1);
    border-radius: 12px;
    box-shadow: 0 5px 20px rgba(0, 0, 0, 0.05);
    overflow: hidden;
}

.section-faq-with-icon-highlight-g4h .faq-item {
    border-bottom: 1px solid var(--color-border);
}

.section-faq-with-icon-highlight-g4h .faq-item:last-child {
    border-bottom: none;
}

.section-faq-with-icon-highlight-g4h .faq-item summary {
    display: flex;
    justify-content: space-between;
    align-items: center;
    cursor: pointer;
    padding: 20px;
    font-family: var(--font-body);
    color: var(--color-dark);
    font-size: 1.1rem;
    font-weight: 500;
    transition: color 0.3s ease;
}

.section-faq-with-icon-highlight-g4h .faq-item[open] summary {
    background-color: var(--color-primary);
    color: var(--color-light);
}

.section-faq-with-icon-highlight-g4h .faq-icon-wrapper {
    width: 30px;
    height: 30px;
    margin-right: 15px;
    display: flex;
    align-items: center;
    justify-content: center;
}

.section-faq-with-icon-highlight-g4h .faq-icon-wrapper img {
    width: 100%;
    height: 100%;
    object-fit: contain;
}

.section-faq-with-icon-highlight-g4h .faq-item[open] .faq-icon-wrapper img {
    filter: invert(1) brightness(1.5);
}

.section-faq-with-icon-highlight-g4h .faq-item summary .faq-question {
    flex-grow: 1;
    margin-right: 15px;
}

.section-faq-with-icon-highlight-g4h .faq-item summary .faq-arrow {
    transition: transform 0.3s ease;
    font-size: 0.8em;
    color: var(--color-secondary);
    display: inline-block;
    width: 10px;
    height: 10px;
    border: solid currentColor;
    border-width: 0 2px 2px 0;
    transform: rotate(-45deg);
}

.section-faq-with-icon-highlight-g4h .faq-item[open] summary .faq-arrow {
    transform: rotate(-135deg);
    color: var(--color-light);
}

.section-faq-with-icon-highlight-g4h .faq-answer {
    padding: 20px;
    background-color: var(--color-light);
    color: var(--color-dark);
    font-family: var(--font-body);
    font-size: 1rem;
    line-height: 1.6;
    border-top: 1px solid var(--color-border);
}

.section-faq-with-icon-highlight-g4h .faq-answer p {
    margin: 0;
}

/* Ensure native markers are hidden */
.section-faq-with-icon-highlight-g4h summary::-webkit-details-marker {
    display: none;
}
.section-faq-with-icon-highlight-g4h summary {
    list-style: none;
}
