css:
.faq-image-accordion-section {
    padding: 4rem 0 0 0;
    background-color: #f0f0f0;
    overflow: hidden;
}
.faq-image-accordion-section .container {
    width: 90%;
    max-width: 800px;
    margin: 0 auto;
    padding: 0 1rem;
}
.faq-image-accordion-section .section-title {
    background-color: #000;
    color: #fff;
    padding: 1.5rem 2rem;
    display: inline-block;
    font-size: clamp(1.5rem, 4vw, 2rem);
    font-weight: 700;
    margin-bottom: 2rem;
    border: 3px dotted #fff;
    outline: 2px solid #000;
}
.faq-image-accordion-section .accordion-container {
    max-width: 700px;
}
.faq-image-accordion-section .faq-item {
    border-bottom: 1px solid #ddd;
}
.faq-image-accordion-section .faq-item:last-of-type {
    border-bottom: none;
}
.faq-image-accordion-section .faq-item summary {
    padding: 1rem 0;
    font-weight: 700;
    color: #000;
    cursor: pointer;
    list-style: none;
    transition: background-color 0.3s ease, color 0.3s ease;
}
.faq-image-accordion-section .faq-item summary::-webkit-details-marker {
    display: none;
}
.faq-image-accordion-section .faq-item[open] > summary {
    background-color: #fbc02d;
    color: #fff;
    padding: 1rem;
}
.faq-image-accordion-section .faq-answer {
    background-color: #fff;
    border: 2px dotted #ddd;
    padding: 1.5rem;
    margin-bottom: 1rem;
}
.faq-image-accordion-section .faq-answer p {
    margin: 0;
    color: #333;
    line-height: 1.6;
}
.faq-image-accordion-section .footer-image {
    margin-top: 4rem;
    width: 100%;
    height: 300px;
}
.faq-image-accordion-section .footer-image img {
    width: 100%;
    height: 100%;
    object-fit: cover;
    filter: grayscale(100%);
}
.faq-image-accordion-section .anim-delay-1 {
    transition-delay: 0.1s;
}
