css:
.features-alternating-section { padding: 5rem 0; position: relative; width: 100%; box-sizing: border-box; }
.features-alternating-section * { box-sizing: border-box; }
.features-alternating-section .container { max-width: 1000px; margin: 0 auto; width: 90%; display: flex; flex-direction: column; gap: 5rem; }
.features-alternating-section .feature-row { display: grid; grid-template-columns: 1fr; gap: 2rem; align-items: center; }
@media(min-width: 768px) { .features-alternating-section .feature-row { grid-template-columns: 1fr 1fr; gap: 4rem; } }
.features-alternating-section .feature-row.reversed .feature-image { order: 2; }
.features-alternating-section .feature-image img { width: 100%; border-radius: 1rem; aspect-ratio: 4/3; object-fit: cover; }
.features-alternating-section .feature-text h3 { font-size: 2rem; font-weight: 700; margin-bottom: 1rem; color: var(--color-dark, #111827); }
.features-alternating-section .feature-text p { color: var(--color-secondary, #4b5563); line-height: 1.7; }
