css:
.features-bento-dark-section { padding: 5rem 0; background-color: var(--color-dark, #111827); color: var(--color-light, #f9fafb); position: relative; width: 100%; box-sizing: border-box; }
.features-bento-dark-section * { box-sizing: border-box; }
.features-bento-dark-section .container { max-width: 900px; margin: 0 auto; padding: 0 1rem; }
.features-bento-dark-section .bento-grid { display: grid; grid-template-columns: repeat(3, 1fr); grid-template-rows: repeat(2, 1fr); gap: 1rem; }
.features-bento-dark-section .bento-item { background-color: var(--color-surface-1, #1f2937); border: 1px solid var(--color-border, #374151); border-radius: 1rem; padding: 1.5rem; }
.features-bento-dark-section .bento-item h4 { font-size: 1.25rem; font-weight: 600; margin-bottom: 0.5rem; color: var(--color-light, #f9fafb); }
.features-bento-dark-section .bento-item p { color: var(--color-secondary, #9ca3af); font-size: 0.9rem; }
.features-bento-dark-section .item-1 { grid-column: 1 / 3; }
.features-bento-dark-section .item-2 { grid-column: 3 / 4; grid-row: 1 / 3; }
.features-bento-dark-section .item-3 { grid-column: 1 / 2; }
.features-bento-dark-section .item-4 { grid-column: 2 / 3; }
@media (max-width: 768px) { .features-bento-dark-section .bento-grid { grid-template-columns: 1fr; grid-template-rows: auto; } .features-bento-dark-section .bento-item { grid-column: auto; grid-row: auto; } }
