css:
.features-comparison-section { padding: 4rem 0; position: relative; width: 100%; box-sizing: border-box; background-color: var(--color-light, #ffffff); }
.features-comparison-section * { box-sizing: border-box; }
.features-comparison-section .container { max-width: 900px; margin: 0 auto; padding: 0 1rem; overflow-x: auto; }
.features-comparison-section .comparison-table { width: 100%; border-collapse: collapse; min-width: 600px; }
.features-comparison-section th, .features-comparison-section td { padding: 1rem; border: 1px solid #e5e7eb; text-align: center; color: var(--color-dark, #111827); }
.features-comparison-section th { background-color: #f9fafb; font-weight: 600; }
.features-comparison-section td:first-child { text-align: left; font-weight: 500; }
