css:
.section-menu-grid-v1 {
  --color-bg: #000000;
  --color-card: #e6e6e6;
  --color-text: #1a1a1a;
  background-color: var(--color-bg);
  padding-bottom: 60px;
}

.section-menu-grid-v1 .menu-header {
  position: relative;
  height: 300px;
  overflow: hidden;
  display: flex;
  align-items: center;
  justify-content: center;
  margin-bottom: 40px;
}

.section-menu-grid-v1 .header-bg {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  object-fit: cover;
  opacity: 0.6;
}

.section-menu-grid-v1 .header-content {
  position: relative;
  z-index: 2;
  text-align: center;
}

.section-menu-grid-v1 .section-title {
  font-size: 3.5rem;
  color: #fff;
  font-weight: 800;
  text-shadow: 0 4px 8px rgba(0,0,0,0.5);
  font-family: 'Helvetica Neue', sans-serif;
  margin: 0;
}

.section-menu-grid-v1 .container {
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 20px;
}

.section-menu-grid-v1 .grid-wrapper {
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 30px;
}

@media (max-width: 992px) {
  .section-menu-grid-v1 .grid-wrapper {
    grid-template-columns: repeat(2, 1fr);
  }
}
@media (max-width: 600px) {
  .section-menu-grid-v1 .grid-wrapper {
    grid-template-columns: 1fr;
  }
}

.section-menu-grid-v1 .menu-card {
  background: var(--color-card);
  padding: 20px;
  text-align: left;
  transition: transform 0.2s;
}

.section-menu-grid-v1 .menu-card:hover {
  transform: translateY(-5px);
}

.section-menu-grid-v1 .card-img {
  width: 100%;
  aspect-ratio: 1/1;
  overflow: hidden;
  margin-bottom: 20px;
  background: #333;
}

.section-menu-grid-v1 .card-img img {
  width: 100%;
  height: 100%;
  object-fit: cover;
}

.section-menu-grid-v1 .card-body h3 {
  font-size: 1.25rem;
  font-weight: 700;
  margin: 0 0 10px 0;
  color: var(--color-text);
}

.section-menu-grid-v1 .card-body p {
  font-size: 0.9rem;
  color: #555;
  line-height: 1.5;
  margin: 0;
}

.section-menu-grid-v1 .credit {
  text-align: center;
  color: #666;
  font-size: 0.8rem;
  margin-top: 40px;
}
