css:
.footer-3col-socials-bottom {
    background-color: var(--color-dark, #0D1B2A);
    color: var(--color-light, #FFFFFF);
    padding: 5rem 1rem 2rem;
    font-family: sans-serif;
    position: relative;
}
.footer-3col-socials-bottom .container {
    max-width: 1200px;
    margin: 0 auto;
}
.footer-3col-socials-bottom .footer-main-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    gap: 3rem;
    margin-bottom: 3rem;
}
.footer-3col-socials-bottom .logotype {
    font-size: 2.5rem;
    font-weight: bold;
    text-decoration: none;
    color: var(--color-light, #FFFFFF);
    display: block;
    margin-bottom: 1rem;
}
.footer-3col-socials-bottom .footer-col > p {
    font-size: 0.95rem;
    color: rgba(255, 255, 255, 0.7);
    line-height: 1.6;
    margin-bottom: 2rem;
}
.footer-3col-socials-bottom .footer-col h4 {
    font-size: 1.1rem;
    font-weight: bold;
    margin-bottom: 1.5rem;
    color: var(--color-light, #FFFFFF);
}
.footer-3col-socials-bottom .contact-item {
    display: flex;
    align-items: center;
    gap: 0.75rem;
    color: rgba(255, 255, 255, 0.9);
    margin-bottom: 1rem;
}
.footer-3col-socials-bottom .footer-col a {
    display: block;
    color: rgba(255, 255, 255, 0.7);
    text-decoration: none;
    margin-bottom: 1rem;
    transition: color 0.3s;
}
.footer-3col-socials-bottom .footer-divider {
    border: none;
    height: 1px;
    background-color: rgba(255, 255, 255, 0.1);
    margin: 0 0 2rem;
}
.footer-3col-socials-bottom .footer-bottom {
    display: flex;
    justify-content: space-between;
    align-items: center;
    flex-wrap: wrap;
    gap: 1.5rem;
    font-size: 0.9rem;
    color: rgba(255, 255, 255, 0.6);
}
.footer-3col-socials-bottom .social-icons {
    display: flex;
    gap: 1.5rem;
    font-size: 1.25rem;
}
.footer-3col-socials-bottom .social-icons a {
    color: var(--color-light, #FFFFFF);
}
.footer-3col-socials-bottom .legal-links {
    display: flex;
    gap: 1.5rem;
}
.footer-3col-socials-bottom .legal-links a {
    text-decoration: underline;
    color: rgba(255, 255, 255, 0.8);
}
